import { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { toast } from 'sonner';
import { 
  UserPlus, 
  Edit, 
  Trash2, 
  Search,
  Phone,
  Mail,
  MapPin,
  Calendar,
  Scissors,
  Star,
  Clock
} from 'lucide-react';
import CreateBarberModal from '@/components/modals/CreateBarberModal';

interface Barber {
  id: number;
  name: string;
  email: string;
  phone: string;
  specialties: string[];
  status: 'ativo' | 'inativo' | 'ferias';
  rating: number;
  workSchedule: string;
  hireDate: string;
  address: string;
  avatar?: string;
}

// Mock data
const initialBarbers: Barber[] = [
  {
    id: 1,
    name: 'JoÃ£o Silva',
    email: 'joao@barbershop.com',
    phone: '(11) 99999-9999',
    specialties: ['Corte Masculino', 'Barba', 'Bigode'],
    status: 'ativo',
    rating: 4.8,
    workSchedule: 'Seg-Sex: 9h-18h',
    hireDate: '2023-01-15',
    address: 'Rua das Flores, 123 - SÃ£o Paulo/SP',
  },
  {
    id: 2,
    name: 'Pedro Santos',
    email: 'pedro@barbershop.com',
    phone: '(11) 88888-8888',
    specialties: ['Corte Moderno', 'DegradÃª', 'Desenho'],
    status: 'ativo',
    rating: 4.9,
    workSchedule: 'Ter-Sab: 10h-19h',
    hireDate: '2023-03-10',
    address: 'Av. Paulista, 456 - SÃ£o Paulo/SP',
  },
  {
    id: 3,
    name: 'Carlos Lima',
    email: 'carlos@barbershop.com',
    phone: '(11) 77777-7777',
    specialties: ['Corte ClÃ¡ssico', 'Barba', 'Sobrancelha'],
    status: 'ferias',
    rating: 4.7,
    workSchedule: 'Seg-Sex: 8h-17h',
    hireDate: '2022-11-20',
    address: 'Rua Augusta, 789 - SÃ£o Paulo/SP',
  },
];

const Barbers = () => {
  const [barbers, setBarbers] = useState<Barber[]>(initialBarbers);
  const [searchTerm, setSearchTerm] = useState('');
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [selectedBarber, setSelectedBarber] = useState<Barber | null>(null);
  const [formData, setFormData] = useState<Partial<Barber>>({
    name: '',
    email: '',
    phone: '',
    specialties: [],
    status: 'ativo',
    workSchedule: '',
    address: '',
  });

  const filteredBarbers = barbers.filter(barber =>
    barber.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    barber.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
    barber.specialties.some(specialty => 
      specialty.toLowerCase().includes(searchTerm.toLowerCase())
    )
  );

  const handleAdd = () => {
    if (!formData.name || !formData.email || !formData.phone) {
      toast.error('Preencha todos os campos obrigatÃ³rios');
      return;
    }

    const newBarber: Barber = {
      id: Date.now(),
      name: formData.name!,
      email: formData.email!,
      phone: formData.phone!,
      specialties: formData.specialties || [],
      status: (formData.status as 'ativo' | 'inativo' | 'ferias') || 'ativo',
      rating: 0,
      workSchedule: formData.workSchedule || '',
      hireDate: new Date().toISOString().split('T')[0],
      address: formData.address || '',
    };

    setBarbers([...barbers, newBarber]);
    setIsAddDialogOpen(false);
    setFormData({
      name: '',
      email: '',
      phone: '',
      specialties: [],
      status: 'ativo',
      workSchedule: '',
      address: '',
    });
    toast.success('Barbeiro adicionado com sucesso!');
  };

  const handleEdit = () => {
    if (!selectedBarber || !formData.name || !formData.email || !formData.phone) {
      toast.error('Preencha todos os campos obrigatÃ³rios');
      return;
    }

    setBarbers(barbers.map(barber =>
      barber.id === selectedBarber.id
        ? {
            ...barber,
            name: formData.name!,
            email: formData.email!,
            phone: formData.phone!,
            specialties: formData.specialties || [],
            status: (formData.status as 'ativo' | 'inativo' | 'ferias') || barber.status,
            workSchedule: formData.workSchedule || barber.workSchedule,
            address: formData.address || barber.address,
          }
        : barber
    ));

    setIsEditDialogOpen(false);
    setSelectedBarber(null);
    setFormData({
      name: '',
      email: '',
      phone: '',
      specialties: [],
      status: 'ativo',
      workSchedule: '',
      address: '',
    });
    toast.success('Barbeiro atualizado com sucesso!');
  };

  const handleDelete = (id: number) => {
    setBarbers(barbers.filter(barber => barber.id !== id));
    toast.success('Barbeiro removido com sucesso!');
  };

  const openEditDialog = (barber: Barber) => {
    setSelectedBarber(barber);
    setFormData({
      name: barber.name,
      email: barber.email,
      phone: barber.phone,
      specialties: barber.specialties,
      status: barber.status,
      workSchedule: barber.workSchedule,
      address: barber.address,
    });
    setIsEditDialogOpen(true);
  };

  const getStatusBadge = (status: string) => {
    const variants = {
      ativo: 'default',
      inativo: 'destructive',
      ferias: 'secondary',
    };
    return variants[status as keyof typeof variants] || 'default';
  };

  const handleSpecialtiesChange = (value: string) => {
    const specialtiesArray = value.split(',').map(s => s.trim()).filter(s => s);
    setFormData({ ...formData, specialties: specialtiesArray });
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">Barbeiros</h1>
          <p className="text-muted-foreground">Gerencie a equipe de barbeiros</p>
        </div>

        <Button onClick={() => setIsCreateModalOpen(true)}>
          <UserPlus className="h-4 w-4 mr-2" />
          Adicionar Barbeiro
        </Button>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center space-x-2">
              <div className="h-8 w-8 bg-primary/10 rounded-full flex items-center justify-center">
                <Scissors className="h-4 w-4 text-primary" />
              </div>
              <div>
                <p className="text-sm font-medium text-muted-foreground">Total</p>
                <p className="text-2xl font-bold">{barbers.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center space-x-2">
              <div className="h-8 w-8 bg-green-100 rounded-full flex items-center justify-center">
                <Scissors className="h-4 w-4 text-green-600" />
              </div>
              <div>
                <p className="text-sm font-medium text-muted-foreground">Ativos</p>
                <p className="text-2xl font-bold text-green-600">
                  {barbers.filter(b => b.status === 'ativo').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center space-x-2">
              <div className="h-8 w-8 bg-yellow-100 rounded-full flex items-center justify-center">
                <Calendar className="h-4 w-4 text-yellow-600" />
              </div>
              <div>
                <p className="text-sm font-medium text-muted-foreground">FÃ©rias</p>
                <p className="text-2xl font-bold text-yellow-600">
                  {barbers.filter(b => b.status === 'ferias').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center space-x-2">
              <div className="h-8 w-8 bg-orange-100 rounded-full flex items-center justify-center">
                <Star className="h-4 w-4 text-orange-600" />
              </div>
              <div>
                <p className="text-sm font-medium text-muted-foreground">AvaliaÃ§Ã£o MÃ©dia</p>
                <p className="text-2xl font-bold text-orange-600">
                  {(barbers.reduce((acc, b) => acc + b.rating, 0) / barbers.length).toFixed(1)}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Search and Table */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>Lista de Barbeiros</CardTitle>
              <CardDescription>Gerencie informaÃ§Ãµes da equipe</CardDescription>
            </div>
            <div className="relative">
              <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Buscar barbeiros..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-9 w-64"
              />
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Barbeiro</TableHead>
                <TableHead>Contato</TableHead>
                <TableHead>Especialidades</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>AvaliaÃ§Ã£o</TableHead>
                <TableHead>HorÃ¡rio</TableHead>
                <TableHead className="text-right">AÃ§Ãµes</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredBarbers.map((barber) => (
                <TableRow key={barber.id}>
                  <TableCell>
                    <div className="flex items-center space-x-3">
                      <Avatar>
                        <AvatarImage src={barber.avatar} />
                        <AvatarFallback>
                          {barber.name.split(' ').map(n => n[0]).join('')}
                        </AvatarFallback>
                      </Avatar>
                      <div>
                        <p className="font-medium">{barber.name}</p>
                        <p className="text-sm text-muted-foreground">
                          Desde {new Date(barber.hireDate).toLocaleDateString('pt-BR')}
                        </p>
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="space-y-1">
                      <div className="flex items-center gap-2 text-sm">
                        <Mail className="h-3 w-3" />
                        {barber.email}
                      </div>
                      <div className="flex items-center gap-2 text-sm">
                        <Phone className="h-3 w-3" />
                        {barber.phone}
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="flex flex-wrap gap-1">
                      {barber.specialties.map((specialty, index) => (
                        <Badge key={index} variant="secondary" className="text-xs">
                          {specialty}
                        </Badge>
                      ))}
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge variant={getStatusBadge(barber.status) as any}>
                      {barber.status}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-1">
                      <Star className="h-4 w-4 fill-yellow-400 text-yellow-400" />
                      <span>{barber.rating.toFixed(1)}</span>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-2 text-sm">
                      <Clock className="h-3 w-3" />
                      {barber.workSchedule}
                    </div>
                  </TableCell>
                  <TableCell className="text-right">
                    <div className="flex justify-end gap-2">
                      <Button 
                        variant="outline" 
                        size="sm"
                        onClick={() => openEditDialog(barber)}
                      >
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button 
                        variant="destructive" 
                        size="sm"
                        onClick={() => handleDelete(barber.id)}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {/* Edit Dialog */}
      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
