              />
            </div>

            <div className="space-y-2 col-span-2">
              <Label htmlFor="edit-workSchedule">HorÃ¡rio de Trabalho</Label>
              <Input
                id="edit-workSchedule"
                value={formData.workSchedule}
                onChange={(e) => setFormData({ ...formData, workSchedule: e.target.value })}
                placeholder="Seg-Sex: 9h-18h"
              />
            </div>

            <div className="space-y-2 col-span-2">
              <Label htmlFor="edit-address">EndereÃ§o</Label>
              <Textarea
                id="edit-address"
                value={formData.address}
                onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                placeholder="Rua, nÃºmero, bairro - Cidade/Estado"
              />
            </div>
          </div>

          <DialogFooter>
            <Button variant="outline" onClick={() => setIsEditDialogOpen(false)}>
              Cancelar
            </Button>
            <Button onClick={handleEdit}>Salvar AlteraÃ§Ãµes</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
};

