import { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { apiService } from '@/lib/api';
import { useBarbershop } from '@/contexts/BarbershopContext';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { toast } from 'sonner';
import { 
  ShoppingCart, 
  Plus, 
  Minus, 
  Trash2, 
  Calculator, 
  CreditCard, 
  Banknote,
  Receipt,
  Search,
  Scissors,
  Package
} from 'lucide-react';

// Mock data
const mockServices = [
  { id: 1, name: 'Corte Masculino', price: 35.00, category: 'serviÃ§o' },
  { id: 2, name: 'Barba', price: 25.00, category: 'serviÃ§o' },
  { id: 3, name: 'Corte + Barba', price: 55.00, category: 'serviÃ§o' },
  { id: 4, name: 'Sobrancelha', price: 15.00, category: 'serviÃ§o' },
];

const mockProducts = [
  { id: 5, name: 'Pomada Modeladora', price: 45.00, category: 'produto', stock: 12 },
  { id: 6, name: 'Shampoo Anticaspa', price: 28.00, category: 'produto', stock: 8 },
  { id: 7, name: 'Cera para Cabelo', price: 38.00, category: 'produto', stock: 15 },
  { id: 8, name: 'Ã“leo para Barba', price: 52.00, category: 'produto', stock: 6 },
];

const mockBarbers = [
  { id: 1, name: 'JoÃ£o Silva' },
  { id: 2, name: 'Pedro Santos' },
  { id: 3, name: 'Carlos Lima' },
];

const mockClients = [
  { id: 1, name: 'Ricardo Oliveira', phone: '(11) 99999-9999' },
  { id: 2, name: 'Felipe Costa', phone: '(11) 88888-8888' },
  { id: 3, name: 'Bruno Alves', phone: '(11) 77777-7777' },
];

interface CartItem {
  id: number;
  name: string;
  price: number;
  category: string;
  quantity: number;
}

interface Sale {
  id: number;
  items: CartItem[];
  total: number;
  paymentMethod: string;
  barber: string;
  client: string;
  date: string;
}

const PDV = () => {
  const [cart, setCart] = useState<CartItem[]>([]);
  const [selectedBarber, setSelectedBarber] = useState('');
  const [selectedClient, setSelectedClient] = useState('');
  const [selectedPayment, setSelectedPayment] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [showReceipt, setShowReceipt] = useState(false);
  const [lastSale, setLastSale] = useState<Sale | null>(null);

  const allItems = [...mockServices, ...mockProducts];

  const filteredItems = allItems.filter(item =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const addToCart = (item: any) => {
    const existingItem = cart.find(cartItem => cartItem.id === item.id);

    if (existingItem) {
      setCart(cart.map(cartItem =>
        cartItem.id === item.id
          ? { ...cartItem, quantity: cartItem.quantity + 1 }
          : cartItem
      ));
    } else {
      setCart([...cart, { ...item, quantity: 1 }]);
    }

    toast.success(`${item.name} adicionado ao carrinho`);
  };

  const updateQuantity = (id: number, change: number) => {
    setCart(cart.map(item => {
      if (item.id === id) {
        const newQuantity = item.quantity + change;
        return newQuantity > 0 ? { ...item, quantity: newQuantity } : item;
      }
      return item;
    }).filter(item => item.quantity > 0));
  };

  const removeFromCart = (id: number) => {
    setCart(cart.filter(item => item.id !== id));
    toast.success('Item removido do carrinho');
  };

  const getTotal = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const finalizeSale = () => {
    if (cart.length === 0) {
      toast.error('Adicione itens ao carrinho');
      return;
    }

    if (!selectedBarber) {
      toast.error('Selecione um barbeiro');
      return;
    }

    if (!selectedPayment) {
      toast.error('Selecione a forma de pagamento');
      return;
    }

    const sale: Sale = {
      id: Date.now(),
      items: [...cart],
      total: getTotal(),
      paymentMethod: selectedPayment,
      barber: mockBarbers.find(b => b.id.toString() === selectedBarber)?.name || '',
      client: selectedClient ? mockClients.find(c => c.id.toString() === selectedClient)?.name || 'Cliente nÃ£o informado' : 'Cliente nÃ£o informado',
      date: new Date().toLocaleString('pt-BR'),
    };

    setLastSale(sale);
    setCart([]);
    setSelectedBarber('');
    setSelectedClient('');
    setSelectedPayment('');
    setShowReceipt(true);

    toast.success('Venda finalizada com sucesso!');
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">PDV - Ponto de Venda</h1>
          <p className="text-muted-foreground">Gerencie vendas de serviÃ§os e produtos</p>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Products and Services */}
        <div className="lg:col-span-2 space-y-4">
          {/* Services Section */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Scissors className="h-5 w-5" />
                ServiÃ§os
              </CardTitle>
              <CardDescription>
                Selecione os serviÃ§os para adicionar Ã  venda
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
                {mockServices.map((service) => (
                  <Card key={service.id} className="cursor-pointer hover:shadow-md transition-shadow">
                    <CardContent className="p-4">
                      <div className="flex items-center justify-between mb-2">
                        <div className="flex items-center gap-2">
                          <Scissors className="h-4 w-4 text-primary" />
                          <h3 className="font-medium text-sm">{service.name}</h3>
                        </div>
                        <Badge variant="default">serviÃ§o</Badge>
                      </div>

                      <div className="flex items-center justify-between">
                        <span className="text-lg font-bold text-primary">
                          R$ {service.price.toFixed(2)}
                        </span>
                      </div>

                      <Button 
                        onClick={() => addToCart(service)}
                        className="w-full mt-3"
                        size="sm"
                      >
                        <Plus className="h-4 w-4 mr-1" />
                        Adicionar
                      </Button>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </CardContent>
          </Card>

          {/* Products Section */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Package className="h-5 w-5" />
                Produtos
              </CardTitle>
              <CardDescription>
                Selecione os produtos para adicionar Ã  venda
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                <Input
                  placeholder="Buscar produtos..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-9"
                />
              </div>

              <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 max-h-64 overflow-y-auto">
                {mockProducts.filter(product => 
                  product.name.toLowerCase().includes(searchTerm.toLowerCase())
                ).map((product) => (
                  <Card key={product.id} className="cursor-pointer hover:shadow-md transition-shadow">
                    <CardContent className="p-4">
                      <div className="flex items-center justify-between mb-2">
                        <div className="flex items-center gap-2">
                          <Package className="h-4 w-4 text-secondary" />
                          <h3 className="font-medium text-sm">{product.name}</h3>
                        </div>
                        <Badge variant="secondary">produto</Badge>
                      </div>

                      <div className="flex items-center justify-between">
                        <span className="text-lg font-bold text-primary">
                          R$ {product.price.toFixed(2)}
                        </span>
                        <span className="text-xs text-muted-foreground">
                          Estoque: {product.stock}
                        </span>
                      </div>

                      <Button 
                        onClick={() => addToCart(product)}
                        className="w-full mt-3"
                        size="sm"
                        disabled={product.stock === 0}
                      >
                        <Plus className="h-4 w-4 mr-1" />
                        {product.stock === 0 ? 'Sem Estoque' : 'Adicionar'}
                      </Button>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Cart and Checkout */}
        <div className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <ShoppingCart className="h-5 w-5" />
                Carrinho
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              {cart.length === 0 ? (
                <p className="text-center text-muted-foreground py-8">
                  Carrinho vazio
                </p>
              ) : (
                <div className="space-y-3 max-h-64 overflow-y-auto">
                  {cart.map((item) => (
                    <div key={item.id} className="flex items-center justify-between p-3 border rounded-lg">
                      <div className="flex-1">
                        <h4 className="font-medium text-sm">{item.name}</h4>
                        <p className="text-sm text-muted-foreground">
                          R$ {item.price.toFixed(2)} x {item.quantity}
                        </p>
                      </div>

                      <div className="flex items-center gap-1">
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => updateQuantity(item.id, -1)}
                        >
                          <Minus className="h-3 w-3" />
                        </Button>
                        <span className="w-8 text-center text-sm">{item.quantity}</span>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => updateQuantity(item.id, 1)}
                        >
                          <Plus className="h-3 w-3" />
                        </Button>
                        <Button
                          size="sm"
                          variant="destructive"
                          onClick={() => removeFromCart(item.id)}
                        >
                          <Trash2 className="h-3 w-3" />
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              )}

              <Separator />

              <div className="space-y-3">
                <div className="flex justify-between items-center text-lg font-bold">
                  <span>Total:</span>
                  <span className="text-primary">R$ {getTotal().toFixed(2)}</span>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Checkout Form */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Calculator className="h-5 w-5" />
                Finalizar Venda
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="barber">Barbeiro *</Label>
                <Select value={selectedBarber} onValueChange={setSelectedBarber}>
                  <SelectTrigger>
                    <SelectValue placeholder="Selecione o barbeiro" />
                  </SelectTrigger>
                  <SelectContent>
                    {mockBarbers.map((barber) => (
                      <SelectItem key={barber.id} value={barber.id.toString()}>
                        {barber.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="client">Cliente (opcional)</Label>
                <Select value={selectedClient} onValueChange={setSelectedClient}>
                  <SelectTrigger>
                    <SelectValue placeholder="Selecione o cliente" />
                  </SelectTrigger>
                  <SelectContent>
                    {mockClients.map((client) => (
                      <SelectItem key={client.id} value={client.id.toString()}>
                        {client.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="payment">Forma de Pagamento *</Label>
                <Select value={selectedPayment} onValueChange={setSelectedPayment}>
                  <SelectTrigger>
                    <SelectValue placeholder="Selecione a forma" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="dinheiro">
                      <span className="flex items-center gap-2">
                        <Banknote className="h-4 w-4" />
                        Dinheiro
                      </span>
                    </SelectItem>
                    <SelectItem value="cartao">
                      <span className="flex items-center gap-2">
                        <CreditCard className="h-4 w-4" />
                        CartÃ£o
                      </span>
                    </SelectItem>
                    <SelectItem value="pix">PIX</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <Button 
                onClick={finalizeSale} 
                className="w-full" 
                size="lg"
                disabled={cart.length === 0}
              >
                <Receipt className="h-4 w-4 mr-2" />
                Finalizar Venda
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>

      {/* Receipt Dialog */}
      <Dialog open={showReceipt} onOpenChange={setShowReceipt}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Comprovante de Venda</DialogTitle>
            <DialogDescription>
              Venda realizada com sucesso
            </DialogDescription>
          </DialogHeader>

          {lastSale && (
            <div className="space-y-4">
              <div className="text-center space-y-2">
                <h3 className="font-bold">BarberShop Pro</h3>
                <p className="text-sm text-muted-foreground">
                  {lastSale.date}
                </p>
              </div>

              <Separator />

              <div className="space-y-2">
                <p><strong>Barbeiro:</strong> {lastSale.barber}</p>
                <p><strong>Cliente:</strong> {lastSale.client}</p>
                <p><strong>Pagamento:</strong> {lastSale.paymentMethod}</p>
              </div>

              <Separator />

              <div className="space-y-2">
                {lastSale.items.map((item) => (
                  <div key={item.id} className="flex justify-between text-sm">
                    <span>{item.quantity}x {item.name}</span>
                    <span>R$ {(item.price * item.quantity).toFixed(2)}</span>
                  </div>
                ))}
              </div>

              <Separator />

              <div className="flex justify-between font-bold text-lg">
                <span>Total:</span>
                <span>R$ {lastSale.total.toFixed(2)}</span>
              </div>
            </div>
          )}

          <DialogFooter>
            <Button onClick={() => setShowReceipt(false)}>
              Fechar
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default PDV;

