import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { useToast } from '@/hooks/use-toast';
import { useBarbershop } from '@/contexts/BarbershopContext';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { apiService, CreateClientData } from '@/lib/api';
import { Loader2 } from 'lucide-react';

interface CreateClientModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

const CreateClientModal = ({ open, onOpenChange }: CreateClientModalProps) => {
  const { toast } = useToast();
  const { currentBarbershop } = useBarbershop();
  const queryClient = useQueryClient();

  const [formData, setFormData] = useState<CreateClientData>({
    barbershop_id: currentBarbershop?.id || '',
    name: '',
    phone: '',
    email: '',
    birthday: '',
    preferences: '',
    notes: ''
  });

  const [errors, setErrors] = useState<Record<string, string>>({});

  const createClientMutation = useMutation({
    mutationFn: (data: CreateClientData) => apiService.clients.create(data),
    onSuccess: () => {
      toast({
        title: "Cliente criado com sucesso!",
        description: "O novo cliente foi adicionado Ã  sua base de dados.",
      });
      queryClient.invalidateQueries({ queryKey: ['clients'] });
      onOpenChange(false);
      resetForm();
    },
    onError: (error: any) => {
      toast({
        title: "Erro ao criar cliente",
        description: error.message || "Ocorreu um erro ao criar o cliente.",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      barbershop_id: currentBarbershop?.id || '',
      name: '',
      phone: '',
      email: '',
      birthday: '',
      preferences: '',
      notes: ''
    });
    setErrors({});
  };

  const validateForm = () => {
    const newErrors: Record<string, string> = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Nome Ã© obrigatÃ³rio';
    }

    if (!formData.phone.trim()) {
      newErrors.phone = 'Telefone Ã© obrigatÃ³rio';
    }

    if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email invÃ¡lido';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    if (!currentBarbershop) {
      toast({
        title: "Erro",
        description: "Nenhuma barbearia selecionada.",
        variant: "destructive",
      });
      return;
    }

    createClientMutation.mutate({
      ...formData,
      barbershop_id: currentBarbershop.id
    });
  };

  const handleInputChange = (field: keyof CreateClientData, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Adicionar Novo Cliente</DialogTitle>
          <DialogDescription>
            Preencha as informaÃ§Ãµes do cliente. Campos obrigatÃ³rios estÃ£o marcados com *.
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid gap-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="name">Nome *</Label>
              <Input
                id="name"
                value={formData.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                placeholder="Nome completo do cliente"
                className={errors.name ? 'border-red-500' : ''}
              />
              {errors.name && (
                <p className="text-sm text-red-500">{errors.name}</p>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="phone">Telefone *</Label>
              <Input
                id="phone"
                value={formData.phone}
                onChange={(e) => handleInputChange('phone', e.target.value)}
                placeholder="(11) 99999-9999"
                className={errors.phone ? 'border-red-500' : ''}
              />
              {errors.phone && (
                <p className="text-sm text-red-500">{errors.phone}</p>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="email">Email</Label>
              <Input
                id="email"
                type="email"
                value={formData.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                placeholder="cliente@email.com"
                className={errors.email ? 'border-red-500' : ''}
              />
              {errors.email && (
                <p className="text-sm text-red-500">{errors.email}</p>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="birthday">Data de Nascimento</Label>
              <Input
                id="birthday"
                type="date"
                value={formData.birthday}
                onChange={(e) => handleInputChange('birthday', e.target.value)}
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="preferences">PreferÃªncias</Label>
              <Input
                id="preferences"
                value={formData.preferences}
                onChange={(e) => handleInputChange('preferences', e.target.value)}
                placeholder="Ex: Barbeiro preferido, tipo de corte..."
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="notes">ObservaÃ§Ãµes</Label>
              <Textarea
                id="notes"
                value={formData.notes}
                onChange={(e) => handleInputChange('notes', e.target.value)}
                placeholder="InformaÃ§Ãµes adicionais sobre o cliente"
                rows={3}
              />
            </div>
          </div>

          <DialogFooter>
            <Button
              type="button"
              variant="outline"
              onClick={() => onOpenChange(false)}
              disabled={createClientMutation.isPending}
            >
              Cancelar
            </Button>
            <Button
              type="submit"
              disabled={createClientMutation.isPending}
            >
              {createClientMutation.isPending && (
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              )}
              Criar Cliente
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
};

export default CreateClientModal;

