     1â†’import express from 'express';
     2â†’import { query } from '../database.js';
     3â†’
     4â†’const router = express.Router();
     5â†’
     6â†’// GET /api/clients - Listar clientes com filtros
     7â†’router.get('/', async (req, res) => {
     8â†’  try {
     9â†’    const {
    10â†’      barbershop_id,
    11â†’      search,
    12â†’      status = 'active',
    13â†’      page = 1,
    14â†’      limit = 50,
    15â†’      sort_by = 'name',
    16â†’      sort_order = 'ASC'
    17â†’    } = req.query;
    18â†’
    19â†’    if (!barbershop_id) {
    20â†’      return res.status(400).json({
    21â†’        success: false,
    22â†’        error: 'barbershop_id Ã© obrigatÃ³rio'
    23â†’      });
    24â†’    }
    25â†’
    26â†’    const offset = (Number(page) - 1) * Number(limit);
    27â†’    const validSortBy = ['name', 'client_since', 'total_visits', 'total_spent'];
    28â†’    const validSortOrder = ['ASC', 'DESC'];
    29â†’
    30â†’    const sortBy = validSortBy.includes(sort_by as string) ? sort_by : 'name';
    31â†’    const sortOrder = validSortOrder.includes(sort_order as string) ? sort_order : 'ASC';
    32â†’
    33â†’    let whereClause = 'WHERE c.barbershop_id = $1';
    34â†’    const params = [barbershop_id];
    35â†’
    36â†’    if (status && status !== 'all') {
    37â†’      whereClause += ` AND c.status = $${params.length + 1}`;
    38â†’      params.push(status);
    39â†’    }
    40â†’
    41â†’    if (search) {
    42â†’      whereClause += ` AND (c.name ILIKE $${params.length + 1} OR c.phone ILIKE $${params.length + 1})`;
    43â†’      params.push(`%${search}%`);
    44â†’    }
    45â†’
    46â†’    // Query principal
    47â†’    const result = await query(`
    48â†’      SELECT
    49â†’        c.id, c.name, c.phone, c.email, c.birth_date,
    50â†’        c.client_since, c.total_visits, c.total_spent, c.status,
    51â†’        c.notes, c.created_at, c.updated_at,
    52â†’        b.name as preferred_barber_name
    53â†’      FROM clients c
    54â†’      LEFT JOIN barbers b ON c.preferred_barber_id = b.id
    55â†’      ${whereClause}
    56â†’      ORDER BY c.${sortBy} ${sortOrder}
    57â†’      LIMIT $${params.length + 1} OFFSET $${params.length + 2}
    58â†’    `, [...params, limit, offset]);
