router.delete('/:id', async (req, res) => {
  try {
    const { id } = req.params;

    // Verificar se o cliente existe
    const checkResult = await query('SELECT id, name FROM clients WHERE id = $1', [id]);
    if (checkResult.rows.length === 0) {
      return res.status(404).json({
        success: false,
        error: 'Cliente nÃ£o encontrado'
      });
    }

    // Verificar se o cliente tem agendamentos
    const appointmentsCheck = await query(
      'SELECT COUNT(*) as count FROM appointments WHERE client_id = $1',
      [id]
    );

    if (parseInt(appointmentsCheck.rows[0].count) > 0) {
      // Se tem agendamentos, apenas desativar
      await query(`
        UPDATE clients
        SET status = 'inactive', updated_at = NOW()
        WHERE id = $1
      `, [id]);

      return res.json({
        success: true,
        message: 'Cliente desativado com sucesso (possui histÃ³rico de agendamentos)'
      });
    } else {
      // Se nÃ£o tem agendamentos, pode deletar
      await query('DELETE FROM clients WHERE id = $1', [id]);

      return res.json({
        success: true,
        message: 'Cliente removido com sucesso'
      });
    }
  } catch (error: any) {
    console.error('Error deleting client:', error);
    res.status(500).json({
      success: false,
      error: 'Erro ao remover cliente',
      message: process.env.NODE_ENV === 'development' ? error.message : undefined
    });
  }
});

// GET /api/clients/:id/history - HistÃ³rico do cliente
router.get('/:id/history', async (req, res) => {
  try {
    const { id } = req.params;
    const { limit = 20, page = 1 } = req.query;

    // Verificar se o cliente existe
    const checkResult = await query('SELECT id, name FROM clients WHERE id = $1', [id]);
    if (checkResult.rows.length === 0) {
      return res.status(404).json({
        success: false,
        error: 'Cliente nÃ£o encontrado'
      });
    }

    const offset = (Number(page) - 1) * Number(limit);

    // Buscar histÃ³rico de agendamentos
    const appointmentsResult = await query(`
      SELECT
        a.id, a.appointment_date, a.appointment_time, a.status, a.price, a.notes,
        b.name as barber_name,
        s.name as service_name, s.category as service_category
      FROM appointments a
      JOIN barbers b ON a.barber_id = b.id
      JOIN services s ON a.service_id = s.id
      WHERE a.client_id = $1
      ORDER BY a.appointment_date DESC, a.appointment_time DESC
      LIMIT $2 OFFSET $3
    `, [id, limit, offset]);

    // Buscar estatÃ­sticas
    const statsResult = await query(`
      SELECT
        COUNT(*) as total_appointments,
        COUNT(*) FILTER (WHERE status = 'completed') as completed_appointments,
        COUNT(*) FILTER (WHERE status = 'cancelled') as cancelled_appointments,
        COALESCE(SUM(price), 0) as total_spent_calculated,
        MIN(appointment_date) as first_appointment,
        MAX(appointment_date) as last_appointment
      FROM appointments
      WHERE client_id = $1
    `, [id]);

    res.json({
      success: true,
      data: {
        appointments: appointmentsResult.rows,
        stats: statsResult.rows[0]
      }
    });
  } catch (error: any) {
    console.error('Error fetching client history:', error);
    res.status(500).json({
      success: false,
      error: 'Erro ao buscar histÃ³rico do cliente',
      message: process.env.NODE_ENV === 'development' ? error.message : undefined
    });
  }
});

// GET /api/clients/search - Busca rÃ¡pida de clientes
router.get('/search', async (req, res) => {
  try {
    const { barbershop_id, q } = req.query;

    if (!barbershop_id || !q) {
      return res.status(400).json({
        success: false,
        error: 'barbershop_id e q (query) sÃ£o obrigatÃ³rios'
      });
    }

    const result = await query(`
      SELECT id, name, phone, status
      FROM clients
      WHERE barbershop_id = $1
        AND (name ILIKE $2 OR phone ILIKE $2)
        AND status = 'active'
      ORDER BY name
      LIMIT 10
    `, [barbershop_id, `%${q}%`]);

    res.json({
      success: true,
      data: result.rows
    });
  } catch (error: any) {
    console.error('Error searching clients:', error);
    res.status(500).json({
      success: false,
      error: 'Erro ao buscar clientes',
      message: process.env.NODE_ENV === 'development' ? error.message : undefined
    });
  }
});

