import express from 'express';
import { MockDatabase } from '../database/mockData.js';

const router = express.Router();

// GET /api/clients - Listar clientes com filtros
router.get('/', async (req, res) => {
  try {
    const {
      barbershop_id,
      search,
      page = 1,
      limit = 50,
      sort_by = 'name',
      sort_order = 'ASC'
    } = req.query;

    if (!barbershop_id) {
      return res.status(400).json({
        success: false,
        error: 'barbershop_id Ã© obrigatÃ³rio'
      });
    }

    let clients = MockDatabase.getClients(barbershop_id as string);

    // Filter by search term
    if (search) {
      const searchTerm = (search as string).toLowerCase();
      clients = clients.filter(client =>
        client.name.toLowerCase().includes(searchTerm) ||
        client.phone.includes(searchTerm) ||
        (client.email && client.email.toLowerCase().includes(searchTerm))
      );
    }

    // Sort
    clients.sort((a, b) => {
      const aVal = a[sort_by as keyof typeof a] || '';
      const bVal = b[sort_by as keyof typeof b] || '';

      if (sort_order === 'DESC') {
        return bVal > aVal ? 1 : -1;
      }
      return aVal > bVal ? 1 : -1;
    });

    // Pagination
    const total = clients.length;
    const offset = (Number(page) - 1) * Number(limit);
    const paginatedClients = clients.slice(offset, offset + Number(limit));
    const totalPages = Math.ceil(total / Number(limit));

    res.json({
      success: true,
      data: paginatedClients.map(client => ({
        id: client.id,
        name: client.name,
        phone: client.phone,
        email: client.email,
        birth_date: client.birthday,
        preferences: client.preferences,
        notes: client.notes,
        total_visits: client.total_visits,
        total_spent: client.total_spent,
        status: 'active',
        created_at: client.created_at
      })),
      pagination: {
        page: Number(page),
        limit: Number(limit),
        total,
        totalPages,
        hasNext: Number(page) < totalPages,
        hasPrev: Number(page) > 1
      }
    });
  } catch (error: any) {
    console.error('Error fetching clients:', error);
    res.status(500).json({
      success: false,
      error: 'Erro ao buscar clientes',
      message: process.env.NODE_ENV === 'development' ? error.message : undefined
    });
  }
});

// GET /api/clients/:id - Buscar cliente por ID
router.get('/:id', async (req, res) => {
  try {
    const { id } = req.params;
    const client = MockDatabase.getClientById(id);

    if (!client) {
      return res.status(404).json({
        success: false,
        error: 'Cliente nÃ£o encontrado'
      });
    }

    res.json({
      success: true,
      data: {
        id: client.id,
        name: client.name,
        phone: client.phone,
        email: client.email,
        birth_date: client.birthday,
        preferences: client.preferences,
        notes: client.notes,
        total_visits: client.total_visits,
        total_spent: client.total_spent,
        status: 'active',
        created_at: client.created_at
      }
    });
  } catch (error: any) {
    console.error('Error fetching client:', error);
    res.status(500).json({
      success: false,
      error: 'Erro ao buscar cliente',
      message: process.env.NODE_ENV === 'development' ? error.message : undefined
    });
  }
});

// POST /api/clients - Criar novo cliente
router.post('/', async (req, res) => {
  try {
    const {
      barbershop_id,
      name,
      phone,
      email,
      birthday,
      preferences,
      notes
    } = req.body;

    // ValidaÃ§Ãµes bÃ¡sicas
    if (!barbershop_id || !name) {
      return res.status(400).json({
        success: false,
        error: 'barbershop_id e name sÃ£o obrigatÃ³rios'
      });
    }

    // Check for duplicate phone
    const existingClients = MockDatabase.getClients(barbershop_id);
    if (phone && existingClients.some(client => client.phone === phone)) {
      return res.status(400).json({
        success: false,
        error: 'JÃ¡ existe um cliente com este telefone nesta barbearia'
      });
    }

    const newClient = MockDatabase.createClient({
      barbershop_id,
      name,
      phone: phone || '',
      email,
      birthday,
      preferences,
      notes
    });

    res.status(201).json({
      success: true,
      data: {
        id: newClient.id,
        name: newClient.name,
        phone: newClient.phone,
        email: newClient.email,
        birth_date: newClient.birthday,
        preferences: newClient.preferences,
        notes: newClient.notes,
        status: 'active',
        created_at: newClient.created_at
      },
      message: 'Cliente criado com sucesso'
    });
  } catch (error: any) {
    console.error('Error creating client:', error);
    res.status(500).json({
      success: false,
      error: 'Erro ao criar cliente',
      message: process.env.NODE_ENV === 'development' ? error.message : undefined
    });
  }
});

// PUT /api/clients/:id - Atualizar cliente
router.put('/:id', async (req, res) => {
  try {
    const { id } = req.params;
    const client = MockDatabase.getClientById(id);

    if (!client) {
      return res.status(404).json({
        success: false,
        error: 'Cliente nÃ£o encontrado'
      });
