    `, [
      original.barbershop_id,
      original.name + name_suffix,
      original.description,
      original.category,
      original.duration,
      original.price,
      original.commission_rate,
      original.image_url
    ]);

    res.status(201).json({
      success: true,
      data: result.rows[0],
      message: 'ServiÃ§o duplicado com sucesso'
    });
  } catch (error: any) {
    console.error('Error duplicating service:', error);
    res.status(500).json({
      success: false,
      error: 'Erro ao duplicar serviÃ§o',
      message: process.env.NODE_ENV === 'development' ? error.message : undefined
    });
  }
});

