import express from 'express';
import { query } from '../database.js';

const router = express.Router();

// GET /api/clients - Listar clientes com filtros
router.get('/', async (req, res) => {
  try {
    const {
      barbershop_id,
      search,
      status = 'active',
      page = 1,
      limit = 50,
      sort_by = 'name',
      sort_order = 'ASC'
    } = req.query;

    if (!barbershop_id) {
      return res.status(400).json({
        success: false,
        error: 'barbershop_id Ã© obrigatÃ³rio'
      });
    }

    const offset = (Number(page) - 1) * Number(limit);
    const validSortBy = ['name', 'client_since', 'total_visits', 'total_spent'];
    const validSortOrder = ['ASC', 'DESC'];

    const sortBy = validSortBy.includes(sort_by as string) ? sort_by : 'name';
    const sortOrder = validSortOrder.includes(sort_order as string) ? sort_order : 'ASC';

    let whereClause = 'WHERE c.barbershop_id = $1';
    const params = [barbershop_id];

    if (status && status !== 'all') {
      whereClause += ` AND c.status = $${params.length + 1}`;
      params.push(status);
    }

    if (search) {
      whereClause += ` AND (c.name ILIKE $${params.length + 1} OR c.phone ILIKE $${params.length + 1})`;
      params.push(`%${search}%`);
    }

    // Query principal
    const result = await query(`
      SELECT
        c.id, c.name, c.phone, c.email, c.birth_date,
        c.client_since, c.total_visits, c.total_spent, c.status,
        c.notes, c.created_at, c.updated_at,
        b.name as preferred_barber_name
      FROM clients c
      LEFT JOIN barbers b ON c.preferred_barber_id = b.id
      ${whereClause}
      ORDER BY c.${sortBy} ${sortOrder}
      LIMIT $${params.length + 1} OFFSET $${params.length + 2}
    `, [...params, limit, offset]);

    // Contar total de registros
    const countResult = await query(`
      SELECT COUNT(*) as total
      FROM clients c
      ${whereClause}
    `, params);

    const total = parseInt(countResult.rows[0].total);
    const totalPages = Math.ceil(total / Number(limit));

    res.json({
      success: true,
      data: result.rows,
      pagination: {
        page: Number(page),
        limit: Number(limit),
        total,
        totalPages,
        hasNext: Number(page) < totalPages,
        hasPrev: Number(page) > 1
      }
    });
  } catch (error: any) {
    console.error('Error fetching clients:', error);
    res.status(500).json({
      success: false,
      error: 'Erro ao buscar clientes',
      message: process.env.NODE_ENV === 'development' ? error.message : undefined
    });
  }
});

// POST /api/clients - Criar novo cliente
router.post('/', async (req, res) => {
  try {
    const {
      barbershop_id,
      name,
      phone,
      email,
      birth_date,
      address,
      preferred_barber_id,
      notes
    } = req.body;

    // ValidaÃ§Ãµes bÃ¡sicas
    if (!barbershop_id || !name) {
      return res.status(400).json({
        success: false,
        error: 'barbershop_id e name sÃ£o obrigatÃ³rios'
      });
    }

    // Verificar se a barbearia existe
    const barbershopCheck = await query('SELECT id FROM barbershops WHERE id = $1', [barbershop_id]);
    if (barbershopCheck.rows.length === 0) {
      return res.status(400).json({
        success: false,
        error: 'Barbearia nÃ£o encontrada'
      });
    }

    // Verificar se jÃ¡ existe cliente com mesmo telefone na barbearia
    if (phone) {
      const phoneCheck = await query(
        'SELECT id FROM clients WHERE barbershop_id = $1 AND phone = $2',
        [barbershop_id, phone]
      );
      if (phoneCheck.rows.length > 0) {
        return res.status(400).json({
          success: false,
          error: 'JÃ¡ existe um cliente com este telefone nesta barbearia'
        });
      }
    }

    const result = await query(`
      INSERT INTO clients (
        barbershop_id, name, phone, email, birth_date,
        address, preferred_barber_id, notes
      )
      VALUES ($1, $2, $3, $4, $5, $6, $7, $8)
      RETURNING id, name, phone, email, birth_date, client_since, status, created_at
    `, [barbershop_id, name, phone, email, birth_date, address, preferred_barber_id, notes]);

    res.status(201).json({
      success: true,
      data: result.rows[0],
      message: 'Cliente criado com sucesso'
    });
  } catch (error: any) {
    console.error('Error creating client:', error);
    res.status(500).json({
      success: false,
      error: 'Erro ao criar cliente',
      message: process.env.NODE_ENV === 'development' ? error.message : undefined
    });
  }
});

export default router;

