import express from 'express';
import { MockDatabase } from '../database/mockData.js';

const router = express.Router();

// GET /api/barbers - Listar barbeiros
router.get('/', async (req, res) => {
  try {
    const { barbershop_id } = req.query;

    if (!barbershop_id) {
      return res.status(400).json({
        success: false,
        error: 'barbershop_id Ã© obrigatÃ³rio'
      });
    }

    const barbers = MockDatabase.getBarbers(barbershop_id as string);

    res.json({
      success: true,
      data: barbers.map(barber => ({
        id: barber.id,
        name: barber.name,
        email: barber.email,
        phone: barber.phone,
        specialty: barber.specialty,
        bio: barber.bio,
        commission_rate: barber.commission_rate,
        working_hours: barber.working_hours,
        days_off: barber.days_off,
        active: barber.active,
        created_at: barber.created_at
