});

// GET /api/appointments/availability - Check available time slots
router.get('/availability', async (req, res) => {
  try {
    const validatedData = availabilitySchema.parse(req.query);

    // Get barber's working hours (assuming 9:00 - 18:00 for now)
    const workingHours = {
      start: '09:00',
      end: '18:00',
      slotDuration: 30 // 30 minutes slots
    };

    // Get existing appointments for the barber on the date
    const existingAppointments = await pool.query(`
      SELECT appointment_time, duration
      FROM appointments
      WHERE barber_id = $1
      AND appointment_date = $2
      AND status NOT IN ('cancelled', 'no_show')
      ORDER BY appointment_time
    `, [validatedData.barber_id, validatedData.date]);

    // Generate all possible time slots
    const timeSlots = [];
    const startTime = new Date(`${validatedData.date} ${workingHours.start}`);
    const endTime = new Date(`${validatedData.date} ${workingHours.end}`);

    let currentTime = new Date(startTime);
    while (currentTime < endTime) {
      timeSlots.push(currentTime.toTimeString().slice(0, 5));
      currentTime.setMinutes(currentTime.getMinutes() + workingHours.slotDuration);
    }

    // Filter out occupied slots
    const availableSlots = timeSlots.filter(slot => {
      const slotTime = new Date(`${validatedData.date} ${slot}`);
      const slotEndTime = new Date(slotTime);
      slotEndTime.setMinutes(slotEndTime.getMinutes() + workingHours.slotDuration);

      return !existingAppointments.rows.some(appointment => {
        const appointmentStart = new Date(`${validatedData.date} ${appointment.appointment_time}`);
        const appointmentEnd = new Date(appointmentStart);
        appointmentEnd.setMinutes(appointmentEnd.getMinutes() + appointment.duration);

        return (
          (slotTime >= appointmentStart && slotTime < appointmentEnd) ||
          (slotEndTime > appointmentStart && slotEndTime <= appointmentEnd) ||
          (slotTime <= appointmentStart && slotEndTime >= appointmentEnd)
        );
      });
    });

    res.json({
      success: true,
      data: {
        date: validatedData.date,
        barber_id: validatedData.barber_id,
        available_slots: availableSlots,
        working_hours: workingHours
      }
    });
  } catch (error) {
    if (error instanceof z.ZodError) {
      return res.status(400).json({
        success: false,
        error: 'Validation error',
        details: error.errors
      });
    }

    console.error('Error checking availability:', error);
    res.status(500).json({
      success: false,
      error: 'Internal server error'
    });
  }
});

// PATCH /api/appointments/:id/status - Update appointment status
router.patch('/:id/status', async (req, res) => {
  try {
    const appointmentId = req.params.id;
    const { status } = req.body;

    if (!uuidValidate(appointmentId)) {
      return res.status(400).json({
        success: false,
        error: 'Invalid appointment ID'
      });
    }

    const validStatuses = ['scheduled', 'confirmed', 'in_progress', 'completed', 'cancelled', 'no_show'];
    if (!validStatuses.includes(status)) {
      return res.status(400).json({
        success: false,
        error: 'Invalid status. Must be one of: ' + validStatuses.join(', ')
      });
    }

    const result = await pool.query(
      'UPDATE appointments SET status = $1, updated_at = CURRENT_TIMESTAMP WHERE id = $2 RETURNING *',
      [status, appointmentId]
    );

    if (result.rows.length === 0) {
      return res.status(404).json({
        success: false,
        error: 'Appointment not found'
      });
    }

    res.json({
      success: true,
      data: result.rows[0]
    });
  } catch (error) {
    console.error('Error updating appointment status:', error);
    res.status(500).json({
      success: false,
      error: 'Internal server error'
    });
  }
});

