    const checkResult = await query('SELECT id, status FROM appointments WHERE id = $1', [id]);
    if (checkResult.rows.length === 0) {
      return res.status(404).json({
        success: false,
        error: 'Agendamento nÃ£o encontrado'
      });
    }

    if (checkResult.rows[0].status === 'completed') {
      return res.status(400).json({
        success: false,
        error: 'NÃ£o Ã© possÃ­vel cancelar agendamento jÃ¡ completado'
      });
    }

    await query(`
      UPDATE appointments
      SET status = 'cancelled',
          notes = COALESCE(notes || ' | ', '') || 'Cancelado: ' || COALESCE($2, 'Sem motivo informado'),
          updated_at = NOW()
      WHERE id = $1
    `, [id, reason]);

    res.json({
      success: true,
      message: 'Agendamento cancelado com sucesso'
    });
  } catch (error: any) {
    console.error('Error canceling appointment:', error);
    res.status(500).json({
      success: false,
      error: 'Erro ao cancelar agendamento',
      message: process.env.NODE_ENV === 'development' ? error.message : undefined
    });
