      });
    }

    // Buscar duraÃ§Ã£o do serviÃ§o
    const serviceResult = await query('SELECT duration FROM services WHERE id = $1', [service_id]);
    if (serviceResult.rows.length === 0) {
      return res.status(400).json({ success: false, error: 'ServiÃ§o nÃ£o encontrado' });
    }

    const serviceDuration = serviceResult.rows[0].duration;

    // Buscar horÃ¡rio de trabalho do barbeiro
    const barberResult = await query('SELECT working_hours FROM barbers WHERE id = $1', [barber_id]);
    if (barberResult.rows.length === 0) {
      return res.status(400).json({ success: false, error: 'Barbeiro nÃ£o encontrado' });
    }

    // Buscar agendamentos existentes
    const appointmentsResult = await query(`
      SELECT appointment_time, duration
      FROM appointments
      WHERE barber_id = $1
        AND appointment_date = $2
        AND status IN ('scheduled', 'confirmed', 'in_progress')
      ORDER BY appointment_time
    `, [barber_id, date]);

    // Gerar slots disponÃ­veis (lÃ³gica simplificada)
    const workingHours = barberResult.rows[0].working_hours;
    const dayOfWeek = new Date(date + 'T00:00:00').toLocaleDateString('en', { weekday: 'long' }).toLowerCase();
    const daySchedule = workingHours[dayOfWeek] || { start: '09:00', end: '18:00', active: true };

    const availableSlots = [];
    if (daySchedule.active) {
      // LÃ³gica simplificada - seria preciso implementar algoritmo mais sofisticado
      const startHour = parseInt(daySchedule.start.split(':')[0]);
      const endHour = parseInt(daySchedule.end.split(':')[0]);

      for (let hour = startHour; hour < endHour; hour++) {
        for (let minute = 0; minute < 60; minute += 30) {
          const timeSlot = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;

          // Verificar se slot nÃ£o conflita com agendamentos existentes
          const hasConflict = appointmentsResult.rows.some(apt => {
            // LÃ³gica simplificada de verificaÃ§Ã£o de conflito
            return apt.appointment_time === timeSlot;
          });

          if (!hasConflict) {
            availableSlots.push(timeSlot);
          }
        }
      }
    }

    res.json({
      success: true,
      data: {
        date,
        service_duration: serviceDuration,
        available_slots: availableSlots
      }
    });
  } catch (error: any) {
    console.error('Error fetching available slots:', error);
    res.status(500).json({
      success: false,
      error: 'Erro ao buscar horÃ¡rios disponÃ­veis',
      message: process.env.NODE_ENV === 'development' ? error.message : undefined
    });
  }
});

export default router;

