    create: (data: CreateBarbershopData) => apiClient.post<ApiResponse<Barbershop>>('/api/barbershops', data),
    update: (id: string, data: UpdateBarbershopData) => apiClient.put<ApiResponse<Barbershop>>(`/api/barbershops/${id}`, data),
