      return apiClient.get<ApiResponse<Client[]>>(`/api/clients?${query}`);
    },
    getById: (id: string) => apiClient.get<ApiResponse<Client>>(`/api/clients/${id}`),
    create: (data: CreateClientData) => apiClient.post<ApiResponse<Client>>('/api/clients', data),
    update: (id: string, data: UpdateClientData) => apiClient.put<ApiResponse<Client>>(`/api/clients/${id}`, data),
  },

  // Barbers
  barbers: {
    getAll: (barbershopId: string) => {
