import express from 'express';
import cors from 'cors';
import helmet from 'helmet';
import morgan from 'morgan';

const app = express();
const PORT = process.env.PORT || 3001;

// Middlewares
app.use(helmet());
app.use(cors({
  origin: process.env.FRONTEND_URL || '*',
  credentials: true
}));
app.use(morgan('combined'));
app.use(express.json());

// Health check
app.get('/health', (req, res) => {
  res.json({
    status: 'ok',
    timestamp: new Date().toISOString(),
    service: 'barbe-backend',
    version: '1.0.0'
  });
});

// Rota principal conforme requisito
app.get('/api/data', (req, res) => {
  const barbershopData = {
    barbershops: [
      {
        id: "bb-001",
        name: "Barbearia Cl√°ssica",
        address: {
          street: "Rua das Flores, 123",
          city: "S√£o Paulo",
          state: "SP",
          zipCode: "01234-567"
        },
        logo_url: "https://placeholder.co/150x150/png",
        plan_id: "premium",
        is_active: true,
        owner_id: "user-001",
        created_at: "2024-01-01T00:00:00Z"
      },
      {
        id: "bb-002",
        name: "Corte & Estilo",
        address: {
          street: "Av. Paulista, 456",
          city: "S√£o Paulo",
          state: "SP",
          zipCode: "01310-100"
        },
        logo_url: "https://placeholder.co/150x150/png",
        plan_id: "basic",
        is_active: true,
        owner_id: "user-002",
        created_at: "2024-02-01T00:00:00Z"
      }
    ],
    services: [
      {
        id: "srv-001",
        barbershop_id: "bb-001",
        name: "Corte Masculino",
        description: "Corte tradicional masculino",
        duration: 30,
        price: 25.00,
        category: "corte"
      },
      {
        id: "srv-002",
        barbershop_id: "bb-001",
        name: "Barba Completa",
        description: "Aparar e modelar barba",
        duration: 20,
        price: 15.00,
        category: "barba"
      }
    ],
    appointments: [
      {
        id: "apt-001",
        barbershop_id: "bb-001",
        client_name: "Jo√£o Silva",
        client_phone: "(11) 99999-9999",
        service_id: "srv-001",
        barber_id: "bar-001",
        date: "2024-12-21",
        time: "14:00",
        status: "confirmado",
        price: 25.00
      }
    ],
    metrics: {
      total_barbershops: 2,
      total_appointments_today: 15,
      total_revenue_month: 4500.00,
      avg_rating: 4.8,
      generated_at: new Date().toISOString()
    }
  };

  res.json(barbershopData);
});

// 404 handler
app.use('*', (req, res) => {
  res.status(404).json({
    error: 'Endpoint n√£o encontrado',
    available_endpoints: ['/health', '/api/data']
  });
});

// Error handler
app.use((err: any, req: express.Request, res: express.Response, next: express.NextFunction) => {
  console.error('Error:', err);
  res.status(500).json({
    error: 'Erro interno do servidor',
    message: process.env.NODE_ENV === 'development' ? err.message : 'Algo deu errado'
  });
});

app.listen(PORT, () => {
  console.log(`üöÄ Servidor rodando na porta ${PORT}`);
  console.log(`üìä Health check: http://localhost:${PORT}/health`);
  console.log(`üì° API Data: http://localhost:${PORT}/api/data`);
});

export default app;

