**backend/src/database.ts**
```typescript
import { Pool } from 'pg';

const pool = new Pool({
  host: process.env.DB_HOST || '5.78.113.107',
  port: parseInt(process.env.DB_PORT || '5432'),
  database: process.env.DB_NAME || 'barber_system',
  user: process.env.DB_USER || 'postgres',
  password: process.env.DB_PASSWORD,
  ssl: process.env.NODE_ENV === 'production' ? { rejectUnauthorized: false } : false,
  max: 20,
  idleTimeoutMillis: 30000,
  connectionTimeoutMillis: 2000,
});

// Test connection
pool.on('connect', () => {
  console.log('‚úÖ PostgreSQL connected successfully');
});

pool.on('error', (err: Error) => {
  console.error('‚ùå PostgreSQL connection error:', err);
});

export const query = async (text: string, params?: any[]) => {
  const start = Date.now();
  try {
    const res = await pool.query(text, params);
    const duration = Date.now() - start;
    console.log('üîç Query executed', { text, duration, rows: res.rowCount });
    return res;
  } catch (error) {
    console.error('‚ùå Query error:', error);
    throw error;
  }
};

export const getClient = () => {
  return pool.connect();
};

export default pool;
```

**backend/Dockerfile**
```dockerfile
# Backend Dockerfile - Node.js Express
FROM node:20-alpine

# Set working directory
WORKDIR /app

# Install curl for health checks
RUN apk add --no-cache curl

# Copy package files
COPY package*.json ./

# Install all dependencies (including dev for build)
RUN npm install

# Copy source code
COPY src/ ./src/
COPY tsconfig.json ./

# Build TypeScript
RUN npm run build

# Remove dev dependencies and source files
RUN npm prune --production && rm -rf src/ tsconfig.json

# Create non-root user
RUN addgroup -g 1001 -S nodejs && adduser -S backend -u 1001 -G nodejs

# Change ownership
RUN chown -R backend:nodejs /app
USER backend

# Expose port
EXPOSE 3001

# Health check
HEALTHCHECK --interval=30s --timeout=3s --start-period=10s --retries=3 \
  CMD curl -f http://localhost:3001/health || exit 1

# Start the application
CMD ["npm", "start"]
```

