import { Router } from 'express';
import bcrypt from 'bcrypt';
import jwt from 'jsonwebtoken';
import pool from '../database.js';
import { authenticateToken, AuthRequest } from '../middleware/auth.js';

const router = Router();
const JWT_SECRET = process.env.JWT_SECRET || 'your-secret-key-change-in-production';
const JWT_EXPIRES_IN = '7d';

// Login de usuÃ¡rio da barbearia
router.post('/login', async (req, res) => {
  try {
    const { email, password } = req.body;

    if (!email || !password) {
      return res.status(400).json({
        success: false,
        error: 'Email e senha sÃ£o obrigatÃ³rios'
      });
    }

    // Buscar usuÃ¡rio na barbearia
    const userQuery = `
      SELECT u.id, u.email, u.password_hash, u.name, u.role, u.barbershop_id, u.is_active,
             b.name as barbershop_name, b.is_active as barbershop_is_active
      FROM users u
      LEFT JOIN barbershops b ON u.barbershop_id = b.id
      WHERE u.email = $1 AND u.is_active = true
    `;

    const userResult = await pool.query(userQuery, [email.toLowerCase().trim()]);

    if (userResult.rows.length === 0) {
      return res.status(401).json({
        success: false,
        error: 'Email ou senha invÃ¡lidos'
      });
    }

    const user = userResult.rows[0];

    // Verificar se a barbearia estÃ¡ ativa
    if (!user.barbershop_is_active) {
      return res.status(403).json({
        success: false,
        error: 'Barbearia inativa. Entre em contato com o suporte.'
      });
    }

    // Verificar senha
    const isValidPassword = await bcrypt.compare(password, user.password_hash);
    if (!isValidPassword) {
      return res.status(401).json({
        success: false,
        error: 'Email ou senha invÃ¡lidos'
      });
    }

    // Gerar token JWT
    const token = jwt.sign(
      {
        userId: user.id,
        email: user.email,
        barbershop_id: user.barbershop_id,
        is_super_admin: false
      },
      JWT_SECRET,
      { expiresIn: JWT_EXPIRES_IN }
    );

    // Atualizar Ãºltimo login
    await pool.query(
      'UPDATE users SET last_login = NOW() WHERE id = $1',
      [user.id]
    );

    // Criar sessÃ£o
    await pool.query(
      'INSERT INTO user_sessions (user_id, token_hash, expires_at) VALUES ($1, $2, $3)',
      [user.id, await bcrypt.hash(token, 10), new Date(Date.now() + 7 * 24 * 60 * 60 * 1000)]
    );

    res.json({
      success: true,
      data: {
        token,
        user: {
          id: user.id,
          email: user.email,
          name: user.name,
          role: user.role,
          barbershop: {
            id: user.barbershop_id,
            name: user.barbershop_name
          }
        }
      }
    });

  } catch (error) {
    console.error('Erro no login:', error);
    res.status(500).json({
      success: false,
      error: 'Erro interno no servidor'
    });
  }
});

// Login de super admin
router.post('/super-admin/login', async (req, res) => {
  try {
    const { email, password } = req.body;

    if (!email || !password) {
      return res.status(400).json({
        success: false,
        error: 'Email e senha sÃ£o obrigatÃ³rios'
      });
    }

    // Buscar super admin
    const adminQuery = `
      SELECT id, email, password_hash, name, is_active
      FROM super_admins
      WHERE email = $1 AND is_active = true
    `;

    const adminResult = await pool.query(adminQuery, [email.toLowerCase().trim()]);

    if (adminResult.rows.length === 0) {
      return res.status(401).json({
        success: false,
        error: 'Email ou senha invÃ¡lidos'
      });
    }

    const admin = adminResult.rows[0];

    // Verificar senha
    const isValidPassword = await bcrypt.compare(password, admin.password_hash);
    if (!isValidPassword) {
      return res.status(401).json({
        success: false,
        error: 'Email ou senha invÃ¡lidos'
      });
    }

    // Gerar token JWT
    const token = jwt.sign(
      {
        userId: admin.id,
        email: admin.email,
        is_super_admin: true
      },
      JWT_SECRET,
      { expiresIn: JWT_EXPIRES_IN }
    );

    // Atualizar Ãºltimo login
    await pool.query(
      'UPDATE super_admins SET last_login = NOW() WHERE id = $1',
      [admin.id]
    );

    // Criar sessÃ£o
    await pool.query(
      'INSERT INTO user_sessions (super_admin_id, token_hash, expires_at) VALUES ($1, $2, $3)',
      [admin.id, await bcrypt.hash(token, 10), new Date(Date.now() + 7 * 24 * 60 * 60 * 1000)]
    );

    res.json({
      success: true,
      data: {
        token,
        user: {
          id: admin.id,
          email: admin.email,
          name: admin.name,
          role: 'super_admin',
          is_super_admin: true
        }
      }
    });

  } catch (error) {
    console.error('Erro no login super admin:', error);
    res.status(500).json({
      success: false,
      error: 'Erro interno no servidor'
    });
  }
});

// Logout
router.post('/logout', authenticateToken, async (req: AuthRequest, res) => {
  try {
    const token = req.headers['authorization']?.split(' ')[1];

    if (token) {
      const tokenHash = await bcrypt.hash(token, 10);
      await pool.query(
        'DELETE FROM user_sessions WHERE token_hash = $1',
        [tokenHash]
      );
    }

    res.json({
      success: true,
      message: 'Logout realizado com sucesso'
    });

  } catch (error) {
    console.error('Erro no logout:', error);
    res.status(500).json({
      success: false,
      error: 'Erro interno no servidor'
    });
  }
});

// Verificar token/usuÃ¡rio atual
router.get('/me', authenticateToken, async (req: AuthRequest, res) => {
  try {
    const user = req.user!;

    let userData: any = {
      id: user.id,
      email: user.email,
      name: user.name,
      role: user.role,
      is_super_admin: user.is_super_admin
    };

    if (!user.is_super_admin && user.barbershop_id) {
      // Buscar informaÃ§Ãµes da barbearia
      const barbershopQuery = 'SELECT id, name, is_active FROM barbershops WHERE id = $1';
      const barbershopResult = await pool.query(barbershopQuery, [user.barbershop_id]);

      if (barbershopResult.rows.length > 0) {
        userData.barbershop = {
          id: barbershopResult.rows[0].id,
          name: barbershopResult.rows[0].name,
          is_active: barbershopResult.rows[0].is_active
        };
      }
    }

    res.json({
      success: true,
      data: userData
    });

  } catch (error) {
    console.error('Erro ao buscar usuÃ¡rio atual:', error);
    res.status(500).json({
      success: false,
      error: 'Erro interno no servidor'
    });
  }
});

// Registrar novo usuÃ¡rio (sÃ³ para admins da barbearia e super admins)
router.post('/register', authenticateToken, async (req: AuthRequest, res) => {
  try {
    const { email, password, name, role, barbershop_id } = req.body;

    // ValidaÃ§Ãµes bÃ¡sicas
    if (!email || !password || !name || !role) {
      return res.status(400).json({
        success: false,
        error: 'Email, senha, nome e role sÃ£o obrigatÃ³rios'
      });
    }

    if (!['admin', 'barbeiro', 'atendente'].includes(role)) {
      return res.status(400).json({
        success: false,
        error: 'Role invÃ¡lida. Use: admin, barbeiro ou atendente'
      });
    }

    let targetBarbershopId = barbershop_id;

    // Super admin pode criar usuÃ¡rio para qualquer barbearia
    if (req.user!.is_super_admin) {
      if (!barbershop_id) {
        return res.status(400).json({
          success: false,
          error: 'Super admin deve especificar barbershop_id'
        });
      }
    } else {
      // UsuÃ¡rio normal sÃ³ pode criar para sua barbearia e deve ser admin
      if (req.user!.role !== 'admin') {
        return res.status(403).json({
          success: false,
          error: 'Apenas administradores podem criar novos usuÃ¡rios'
        });
      }
      targetBarbershopId = req.user!.barbershop_id;
    }

    // Verificar se email jÃ¡ existe
