import express from 'express';
import { query } from '../database.js';
import { authenticateToken, requireBarbershopAccess, AuthRequest } from '../middleware/auth.js';

const router = express.Router();

// GET /api/clients - Listar clientes com filtros
router.get('/', authenticateToken, requireBarbershopAccess, async (req: AuthRequest, res) => {
  try {
    const {
      barbershop_id,
      search,
      status = 'active',
      page = 1,
      limit = 50,
      sort_by = 'name',
      sort_order = 'ASC'
    } = req.query;

    if (!barbershop_id) {
      return res.status(400).json({
        success: false,
        error: 'barbershop_id Ã© obrigatÃ³rio'
      });
    }

    const offset = (Number(page) - 1) * Number(limit);
    const validSortBy = ['name', 'client_since', 'total_visits', 'total_spent'];
    const validSortOrder = ['ASC', 'DESC'];

    const sortBy = validSortBy.includes(sort_by as string) ? sort_by : 'name';
    const sortOrder = validSortOrder.includes(sort_order as string) ? sort_order : 'ASC';

    let whereClause = 'WHERE c.barbershop_id = $1';
    const params = [barbershop_id];

    if (status && status !== 'all') {
      whereClause += ` AND c.status = $${params.length + 1}`;
      params.push(status);
    }

    if (search) {
      whereClause += ` AND (c.name ILIKE $${params.length + 1} OR c.phone ILIKE $${params.length + 1})`;
      params.push(`%${search}%`);
    }

    // Query principal
    const result = await query(`
      SELECT
        c.id, c.name, c.phone, c.email, c.birth_date,
        c.client_since, c.total_visits, c.total_spent, c.status,
        c.notes, c.created_at, c.updated_at,
        b.name as preferred_barber_name
      FROM clients c
      LEFT JOIN barbers b ON c.preferred_barber_id = b.id
      ${whereClause}
      ORDER BY c.${sortBy} ${sortOrder}
      LIMIT $${params.length + 1} OFFSET $${params.length + 2}
    `, [...params, limit, offset]);

    // Contar total de registros
    const countResult = await query(`
      SELECT COUNT(*) as total
      FROM clients c
      ${whereClause}
    `, params);

    const total = parseInt(countResult.rows[0].total);
    const totalPages = Math.ceil(total / Number(limit));

    res.json({
      success: true,
      data: result.rows,
      pagination: {
        page: Number(page),
        limit: Number(limit),
        total,
        totalPages,
        hasNext: Number(page) < totalPages,
        hasPrev: Number(page) > 1
      }
    });
  } catch (error: any) {
    console.error('Error fetching clients:', error);
    res.status(500).json({
      success: false,
      error: 'Erro ao buscar clientes',
      message: process.env.NODE_ENV === 'development' ? error.message : undefined
    });
  }
});

