      WHERE id = $1
      RETURNING id, name, slug, logo_url, address, phone, email, plan_id, is_active, settings, updated_at
    `, [id, name, slug, logo_url, address, phone, email, plan_id, is_active, settings]);

    res.json({
      success: true,
