    console.error('Error fetching dashboard metrics:', error);
    res.status(500).json({
      success: false,
      error: 'Erro ao buscar mÃ©tricas do dashboard',
      message: process.env.NODE_ENV === 'development' ? error.message : undefined
    });
  }
});

