import { Request, Response, NextFunction } from 'express';
import { LoggedRequest } from './requestLogger.js';

export interface ErrorWithDetails extends Error {
  statusCode?: number;
  details?: any;
  code?: string;
}

export const errorHandler = (
  err: ErrorWithDetails,
  req: LoggedRequest,
  res: Response,
  next: NextFunction
) => {
  const requestId = req.requestId || 'unknown';
  const timestamp = new Date().toISOString();

  // Log detalhado do erro
  console.error(`ğŸ’¥ [${timestamp}] UNHANDLED ERROR`, {
    requestId,
    method: req.method,
    url: req.url,
    error: {
      name: err.name,
      message: err.message,
      stack: err.stack,
      code: err.code,
      details: err.details,
      statusCode: err.statusCode
    },
    headers: req.headers,
    body: req.body,
    params: req.params,
    query: req.query,
    timestamp
  });

  // Determinar status code
  const statusCode = err.statusCode || 500;

  // Resposta baseada no ambiente
  const isDevelopment = process.env.NODE_ENV === 'development';

  const errorResponse = {
    success: false,
    error: err.message || 'Internal Server Error',
    requestId,
    timestamp,
    ...(isDevelopment && {
      stack: err.stack,
      details: err.details,
      code: err.code
    })
  };

  res.status(statusCode).json(errorResponse);
};

export const notFoundHandler = (req: LoggedRequest, res: Response) => {
  const requestId = req.requestId || 'unknown';
  const timestamp = new Date().toISOString();

  console.warn(`âš ï¸ [${timestamp}] 404 NOT FOUND`, {
    requestId,
    method: req.method,
    url: req.url,
    originalUrl: req.originalUrl,
    path: req.path,
    headers: req.headers,
    timestamp,
    availableRoutes: getAvailableRoutes()
  });

  res.status(404).json({
    success: false,
    error: 'Endpoint nÃ£o encontrado',
    requestId,
    timestamp,
    requestedUrl: req.originalUrl,
    method: req.method,
    availableEndpoints: getAvailableRoutes()
  });
};

function getAvailableRoutes(): string[] {
  return [
    'GET /health',
    'GET /api/data',
    'POST /api/auth/login',
    'POST /api/auth/super-admin/login',
    'POST /api/auth/logout',
    'GET /api/auth/me',
    'POST /api/auth/register',
    'GET /api/barbershops',
