    'GET /api/barbers',
    'POST /api/barbers',
    'GET /api/services',
    'POST /api/services',
    'GET /api/appointments',
    'POST /api/appointments',
    'GET /api/products',
    'POST /api/products',
    'GET /api/sales',
    'POST /api/sales'
  ];
}

export default { errorHandler, notFoundHandler };

