import { Request, Response, NextFunction } from 'express';
import { v4 as uuidv4 } from 'uuid';

export interface LoggedRequest extends Request {
  requestId: string;
  startTime: number;
}

export const requestLogger = (req: LoggedRequest, res: Response, next: NextFunction) => {
  // Gerar ID Ãºnico para a requisiÃ§Ã£o
  req.requestId = uuidv4();
  req.startTime = Date.now();

  // Log da requisiÃ§Ã£o inicial
  console.log(`ğŸ”µ [${new Date().toISOString()}] REQUEST START`, {
    requestId: req.requestId,
    method: req.method,
    url: req.url,
    path: req.path,
    originalUrl: req.originalUrl,
    headers: {
      'content-type': req.headers['content-type'],
      'authorization': req.headers.authorization ? '[PRESENTE]' : '[AUSENTE]',
      'user-agent': req.headers['user-agent'],
      'x-forwarded-for': req.headers['x-forwarded-for'],
      'host': req.headers.host
    },
    body: req.method === 'POST' || req.method === 'PUT' ?
      sanitizeBody(req.body) : undefined,
    query: req.query,
    params: req.params,
    ip: req.ip,
    timestamp: new Date().toISOString()
  });

  // Override do res.json para capturar resposta
  const originalJson = res.json;
  res.json = function(data: any) {
    const duration = Date.now() - req.startTime;

    console.log(`ğŸŸ¢ [${new Date().toISOString()}] REQUEST END`, {
      requestId: req.requestId,
      method: req.method,
      url: req.url,
      statusCode: res.statusCode,
      duration: `${duration}ms`,
      responseSize: JSON.stringify(data).length,
      success: res.statusCode < 400,
      timestamp: new Date().toISOString()
    });

    return originalJson.call(this, data);
  };

  // Override do res.status para capturar erros
  const originalStatus = res.status;
  res.status = function(code: number) {
    if (code >= 400) {
      console.log(`ğŸ”´ [${new Date().toISOString()}] ERROR STATUS`, {
        requestId: req.requestId,
        method: req.method,
        url: req.url,
        statusCode: code,
        timestamp: new Date().toISOString()
      });
    }
    return originalStatus.call(this, code);
  };

  // Capturar erros nÃ£o tratados
  const originalSend = res.send;
  res.send = function(data: any) {
    const duration = Date.now() - req.startTime;

    if (res.statusCode >= 400) {
      console.log(`ğŸ”´ [${new Date().toISOString()}] ERROR RESPONSE`, {
        requestId: req.requestId,
        method: req.method,
        url: req.url,
        statusCode: res.statusCode,
        duration: `${duration}ms`,
        errorData: typeof data === 'string' ? data : JSON.stringify(data),
        timestamp: new Date().toISOString()
      });
    }

    return originalSend.call(this, data);
  };

  next();
};

// Sanitizar dados sensÃ­veis do body
function sanitizeBody(body: any): any {
  if (!body || typeof body !== 'object') return body;

  const sanitized = { ...body };
  const sensitiveFields = ['password', 'senha', 'token', 'secret', 'key'];

  for (const field of sensitiveFields) {
    if (sanitized[field]) {
      sanitized[field] = '[REDACTED]';
    }
  }

  return sanitized;
}

export default requestLogger;

