     1‚Üíimport { useState, useEffect } from 'react';
     2‚Üíimport { useQuery } from '@tanstack/react-query';
     3‚Üíimport { apiService } from '@/lib/api';
     4‚Üíimport { useBarbershop } from '@/contexts/BarbershopContext';
     5‚Üíimport { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
     6‚Üíimport { Button } from '@/components/ui/button';
     7‚Üíimport { Input } from '@/components/ui/input';
     8‚Üíimport { Label } from '@/components/ui/label';
     9‚Üíimport { Badge } from '@/components/ui/badge';
    10‚Üíimport { Separator } from '@/components/ui/separator';
    11‚Üíimport { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
    12‚Üíimport { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
    13‚Üíimport { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
    14‚Üíimport { toast } from 'sonner';
    15‚Üíimport { 
    16‚Üí  ShoppingCart, 
    17‚Üí  Plus, 
    18‚Üí  Minus, 
    19‚Üí  Trash2, 
    20‚Üí  Calculator, 
    21‚Üí  CreditCard, 
    22‚Üí  Banknote,
    23‚Üí  Receipt,
    24‚Üí  Search,
    25‚Üí  Scissors,
    26‚Üí  Package
    27‚Üí} from 'lucide-react';
    28‚Üí
    29‚Üí// Mock data
    30‚Üíconst mockServices = [
    31‚Üí  { id: 1, name: 'Corte Masculino', price: 35.00, category: 'servi√ßo' },
    32‚Üí  { id: 2, name: 'Barba', price: 25.00, category: 'servi√ßo' },
    33‚Üí  { id: 3, name: 'Corte + Barba', price: 55.00, category: 'servi√ßo' },
    34‚Üí  { id: 4, name: 'Sobrancelha', price: 15.00, category: 'servi√ßo' },
    35‚Üí];
    36‚Üí
    37‚Üíconst mockProducts = [
    38‚Üí  { id: 5, name: 'Pomada Modeladora', price: 45.00, category: 'produto', stock: 12 },
    39‚Üí  { id: 6, name: 'Shampoo Anticaspa', price: 28.00, category: 'produto', stock: 8 },
    40‚Üí  { id: 7, name: 'Cera para Cabelo', price: 38.00, category: 'produto', stock: 15 },
    41‚Üí  { id: 8, name: '√ìleo para Barba', price: 52.00, category: 'produto', stock: 6 },
    42‚Üí];
    43‚Üí
    44‚Üíconst mockBarbers = [
    45‚Üí  { id: 1, name: 'Jo√£o Silva' },
    46‚Üí  { id: 2, name: 'Pedro Santos' },
    47‚Üí  { id: 3, name: 'Carlos Lima' },
    48‚Üí];
    49‚Üí
    50‚Üíconst mockClients = [
    51‚Üí  { id: 1, name: 'Ricardo Oliveira', phone: '(11) 99999-9999' },
    52‚Üí  { id: 2, name: 'Felipe Costa', phone: '(11) 88888-8888' },
    53‚Üí  { id: 3, name: 'Bruno Alves', phone: '(11) 77777-7777' },
    54‚Üí];
    55‚Üí
    56‚Üíinterface CartItem {
    57‚Üí  id: number;
    58‚Üí  name: string;
    59‚Üí  price: number;
    60‚Üí  category: string;
    61‚Üí  quantity: number;
    62‚Üí}
    63‚Üí
    64‚Üíinterface Sale {
    65‚Üí  id: number;
    66‚Üí  items: CartItem[];
    67‚Üí  total: number;
    68‚Üí  paymentMethod: string;
    69‚Üí  barber: string;
    70‚Üí  client: string;
    71‚Üí  date: string;
    72‚Üí}
    73‚Üí
    74‚Üíconst PDV = () => {
    75‚Üí  const { currentBarbershop } = useBarbershop();
    76‚Üí  const [cart, setCart] = useState<CartItem[]>([]);
    77‚Üí  const [selectedBarber, setSelectedBarber] = useState('');
    78‚Üí  const [selectedClient, setSelectedClient] = useState('');
    79‚Üí  const [selectedPayment, setSelectedPayment] = useState('');
    80‚Üí  const [searchTerm, setSearchTerm] = useState('');
    81‚Üí  const [showReceipt, setShowReceipt] = useState(false);
    82‚Üí  const [lastSale, setLastSale] = useState<Sale | null>(null);
    83‚Üí
    84‚Üí  // Fetch real data from APIs
    85‚Üí  const { data: servicesResponse } = useQuery({
    86‚Üí    queryKey: ['services', currentBarbershop?.id],
    87‚Üí    queryFn: () => apiService.getServices(),
    88‚Üí    enabled: !!currentBarbershop?.id
    89‚Üí  });
    90‚Üí
    91‚Üí  const { data: productsResponse } = useQuery({
    92‚Üí    queryKey: ['products', currentBarbershop?.id],
    93‚Üí    queryFn: () => apiService.getProducts(),
    94‚Üí    enabled: !!currentBarbershop?.id
    95‚Üí  });
    96‚Üí
    97‚Üí  const { data: barbersResponse } = useQuery({
    98‚Üí    queryKey: ['barbers', currentBarbershop?.id],
    99‚Üí    queryFn: () => apiService.getBarbers(),
   100‚Üí    enabled: !!currentBarbershop?.id
   101‚Üí  });
   102‚Üí
   103‚Üí  const { data: clientsResponse } = useQuery({
   104‚Üí    queryKey: ['clients', currentBarbershop?.id],
   105‚Üí    queryFn: () => apiService.getClients(),
   106‚Üí    enabled: !!currentBarbershop?.id
   107‚Üí  });
   108‚Üí
   109‚Üí  // Use real data or fallback to mock
   110‚Üí  const services = servicesResponse?.services || mockServices;
   111‚Üí  const products = productsResponse?.products || mockProducts;
   112‚Üí  const barbers = barbersResponse?.barbers || mockBarbers;
   113‚Üí  const clients = clientsResponse?.clients || mockClients;
   114‚Üí
   115‚Üí  const allItems = [
   116‚Üí    ...services.map(s => ({ ...s, category: 'servi√ßo' })),
   117‚Üí    ...products.map(p => ({ ...p, category: 'produto' }))
   118‚Üí  ];
   119‚Üí  
   120‚Üí  const filteredItems = allItems.filter(item =>
   121‚Üí    item.name.toLowerCase().includes(searchTerm.toLowerCase())
   122‚Üí  );
   123‚Üí
   124‚Üí  const addToCart = (item: any) => {
   125‚Üí    const existingItem = cart.find(cartItem => cartItem.id === item.id);
   126‚Üí    
   127‚Üí    if (existingItem) {
   128‚Üí      setCart(cart.map(cartItem =>
   129‚Üí        cartItem.id === item.id
   130‚Üí          ? { ...cartItem, quantity: cartItem.quantity + 1 }
   131‚Üí          : cartItem
   132‚Üí      ));
   133‚Üí    } else {
   134‚Üí      setCart([...cart, { ...item, quantity: 1 }]);
   135‚Üí    }
   136‚Üí    
   137‚Üí    toast.success(`${item.name} adicionado ao carrinho`);
   138‚Üí  };
   139‚Üí
   140‚Üí  const updateQuantity = (id: number, change: number) => {
   141‚Üí    setCart(cart.map(item => {
   142‚Üí      if (item.id === id) {
   143‚Üí        const newQuantity = item.quantity + change;
   144‚Üí        return newQuantity > 0 ? { ...item, quantity: newQuantity } : item;
   145‚Üí      }
   146‚Üí      return item;
   147‚Üí    }).filter(item => item.quantity > 0));
   148‚Üí  };
   149‚Üí
   150‚Üí  const removeFromCart = (id: number) => {
   151‚Üí    setCart(cart.filter(item => item.id !== id));
   152‚Üí    toast.success('Item removido do carrinho');
   153‚Üí  };
   154‚Üí
   155‚Üí  const getTotal = () => {
   156‚Üí    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
   157‚Üí  };
   158‚Üí
   159‚Üí  const finalizeSale = () => {
   160‚Üí    if (cart.length === 0) {
   161‚Üí      toast.error('Adicione itens ao carrinho');
   162‚Üí      return;
   163‚Üí    }
   164‚Üí    
   165‚Üí    if (!selectedBarber) {
   166‚Üí      toast.error('Selecione um barbeiro');
   167‚Üí      return;
   168‚Üí    }
   169‚Üí    
   170‚Üí    if (!selectedPayment) {
   171‚Üí      toast.error('Selecione a forma de pagamento');
   172‚Üí      return;
   173‚Üí    }
   174‚Üí
   175‚Üí    const sale: Sale = {
   176‚Üí      id: Date.now(),
   177‚Üí      items: [...cart],
   178‚Üí      total: getTotal(),
   179‚Üí      paymentMethod: selectedPayment,
   180‚Üí      barber: barbers.find(b => b.id.toString() === selectedBarber)?.name || '',
   181‚Üí      client: selectedClient ? clients.find(c => c.id.toString() === selectedClient)?.name || 'Cliente n√£o informado' : 'Cliente n√£o informado',
   182‚Üí      date: new Date().toLocaleString('pt-BR'),
   183‚Üí    };
   184‚Üí
   185‚Üí    setLastSale(sale);
   186‚Üí    setCart([]);
   187‚Üí    setSelectedBarber('');
   188‚Üí    setSelectedClient('');
   189‚Üí    setSelectedPayment('');
   190‚Üí    setShowReceipt(true);
   191‚Üí    
   192‚Üí    toast.success('Venda finalizada com sucesso!');
   193‚Üí  };
   194‚Üí
   195‚Üí  return (
   196‚Üí    <div className="space-y-6">
   197‚Üí      <div className="flex items-center justify-between">
   198‚Üí        <div>
   199‚Üí          <h1 className="text-3xl font-bold tracking-tight">PDV - Ponto de Venda</h1>
   200‚Üí          <p className="text-muted-foreground">Gerencie vendas de servi√ßos e produtos</p>
   201‚Üí        </div>
   202‚Üí      </div>
   203‚Üí
   204‚Üí      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
   205‚Üí        {/* Products and Services */}
   206‚Üí        <div className="lg:col-span-2 space-y-4">
   207‚Üí          {/* Services Section */}
   208‚Üí          <Card>
   209‚Üí            <CardHeader>
   210‚Üí              <CardTitle className="flex items-center gap-2">
   211‚Üí                <Scissors className="h-5 w-5" />
   212‚Üí                Servi√ßos
   213‚Üí              </CardTitle>
   214‚Üí              <CardDescription>
   215‚Üí                Selecione os servi√ßos para adicionar √† venda
   216‚Üí              </CardDescription>
   217‚Üí            </CardHeader>
   218‚Üí            <CardContent className="space-y-4">
   219‚Üí              <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
   220‚Üí                {mockServices.map((service) => (
   221‚Üí                  <Card key={service.id} className="cursor-pointer hover:shadow-md transition-shadow">
   222‚Üí                    <CardContent className="p-4">
   223‚Üí                      <div className="flex items-center justify-between mb-2">
   224‚Üí                        <div className="flex items-center gap-2">
   225‚Üí                          <Scissors className="h-4 w-4 text-primary" />
   226‚Üí                          <h3 className="font-medium text-sm">{service.name}</h3>
   227‚Üí                        </div>
   228‚Üí                        <Badge variant="default">servi√ßo</Badge>
   229‚Üí                      </div>
   230‚Üí                      
   231‚Üí                      <div className="flex items-center justify-between">
   232‚Üí                        <span className="text-lg font-bold text-primary">
   233‚Üí                          R$ {service.price.toFixed(2)}
   234‚Üí                        </span>
   235‚Üí                      </div>
   236‚Üí                      
   237‚Üí                      <Button 
   238‚Üí                        onClick={() => addToCart(service)}
   239‚Üí                        className="w-full mt-3"
   240‚Üí                        size="sm"
   241‚Üí                      >
   242‚Üí                        <Plus className="h-4 w-4 mr-1" />
   243‚Üí                        Adicionar
   244‚Üí                      </Button>
   245‚Üí                    </CardContent>
   246‚Üí                  </Card>
   247‚Üí                ))}
   248‚Üí              </div>
   249‚Üí            </CardContent>
   250‚Üí          </Card>
   251‚Üí
   252‚Üí          {/* Products Section */}
   253‚Üí          <Card>
   254‚Üí            <CardHeader>
   255‚Üí              <CardTitle className="flex items-center gap-2">
   256‚Üí                <Package className="h-5 w-5" />
   257‚Üí                Produtos
   258‚Üí              </CardTitle>
   259‚Üí              <CardDescription>
   260‚Üí                Selecione os produtos para adicionar √† venda
   261‚Üí              </CardDescription>
   262‚Üí            </CardHeader>
   263‚Üí            <CardContent className="space-y-4">
   264‚Üí              <div className="relative">
   265‚Üí                <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
   266‚Üí                <Input
   267‚Üí                  placeholder="Buscar produtos..."
   268‚Üí                  value={searchTerm}
   269‚Üí                  onChange={(e) => setSearchTerm(e.target.value)}
   270‚Üí                  className="pl-9"
   271‚Üí                />
   272‚Üí              </div>
   273‚Üí              
   274‚Üí              <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 max-h-64 overflow-y-auto">
   275‚Üí                {mockProducts.filter(product => 
   276‚Üí                  product.name.toLowerCase().includes(searchTerm.toLowerCase())
   277‚Üí                ).map((product) => (
   278‚Üí                  <Card key={product.id} className="cursor-pointer hover:shadow-md transition-shadow">
   279‚Üí                    <CardContent className="p-4">
   280‚Üí                      <div className="flex items-center justify-between mb-2">
   281‚Üí                        <div className="flex items-center gap-2">
   282‚Üí                          <Package className="h-4 w-4 text-secondary" />
   283‚Üí                          <h3 className="font-medium text-sm">{product.name}</h3>
   284‚Üí                        </div>
   285‚Üí                        <Badge variant="secondary">produto</Badge>
   286‚Üí                      </div>
   287‚Üí                      
   288‚Üí                      <div className="flex items-center justify-between">
   289‚Üí                        <span className="text-lg font-bold text-primary">
   290‚Üí                          R$ {product.price.toFixed(2)}
   291‚Üí                        </span>
   292‚Üí                        <span className="text-xs text-muted-foreground">
   293‚Üí                          Estoque: {product.stock}
   294‚Üí                        </span>
   295‚Üí                      </div>
   296‚Üí                      
   297‚Üí                      <Button 
   298‚Üí                        onClick={() => addToCart(product)}
   299‚Üí                        className="w-full mt-3"
   300‚Üí                        size="sm"
   301‚Üí                        disabled={product.stock === 0}
   302‚Üí                      >
   303‚Üí                        <Plus className="h-4 w-4 mr-1" />
   304‚Üí                        {product.stock === 0 ? 'Sem Estoque' : 'Adicionar'}
   305‚Üí                      </Button>
   306‚Üí                    </CardContent>
   307‚Üí                  </Card>
   308‚Üí                ))}
   309‚Üí              </div>
   310‚Üí            </CardContent>
   311‚Üí          </Card>
   312‚Üí        </div>
   313‚Üí
   314‚Üí        {/* Cart and Checkout */}
   315‚Üí        <div className="space-y-4">
   316‚Üí          <Card>
   317‚Üí            <CardHeader>
   318‚Üí              <CardTitle className="flex items-center gap-2">
   319‚Üí                <ShoppingCart className="h-5 w-5" />
   320‚Üí                Carrinho
   321‚Üí              </CardTitle>
   322‚Üí            </CardHeader>
   323‚Üí            <CardContent className="space-y-4">
   324‚Üí              {cart.length === 0 ? (
   325‚Üí                <p className="text-center text-muted-foreground py-8">
   326‚Üí                  Carrinho vazio
   327‚Üí                </p>
   328‚Üí              ) : (
   329‚Üí                <div className="space-y-3 max-h-64 overflow-y-auto">
   330‚Üí                  {cart.map((item) => (
   331‚Üí                    <div key={item.id} className="flex items-center justify-between p-3 border rounded-lg">
   332‚Üí                      <div className="flex-1">
   333‚Üí                        <h4 className="font-medium text-sm">{item.name}</h4>
   334‚Üí                        <p className="text-sm text-muted-foreground">
   335‚Üí                          R$ {item.price.toFixed(2)} x {item.quantity}
   336‚Üí                        </p>
   337‚Üí                      </div>
   338‚Üí                      
   339‚Üí                      <div className="flex items-center gap-1">
   340‚Üí                        <Button
   341‚Üí                          size="sm"
   342‚Üí                          variant="outline"
   343‚Üí                          onClick={() => updateQuantity(item.id, -1)}
   344‚Üí                        >
   345‚Üí                          <Minus className="h-3 w-3" />
   346‚Üí                        </Button>
   347‚Üí                        <span className="w-8 text-center text-sm">{item.quantity}</span>
   348‚Üí                        <Button
   349‚Üí                          size="sm"
   350‚Üí                          variant="outline"
   351‚Üí                          onClick={() => updateQuantity(item.id, 1)}
   352‚Üí                        >
   353‚Üí                          <Plus className="h-3 w-3" />
   354‚Üí                        </Button>
   355‚Üí                        <Button
   356‚Üí                          size="sm"
   357‚Üí                          variant="destructive"
   358‚Üí                          onClick={() => removeFromCart(item.id)}
   359‚Üí                        >
   360‚Üí                          <Trash2 className="h-3 w-3" />
   361‚Üí                        </Button>
   362‚Üí                      </div>
   363‚Üí                    </div>
   364‚Üí                  ))}
   365‚Üí                </div>
   366‚Üí              )}
   367‚Üí              
   368‚Üí              <Separator />
   369‚Üí              
   370‚Üí              <div className="space-y-3">
   371‚Üí                <div className="flex justify-between items-center text-lg font-bold">
   372‚Üí                  <span>Total:</span>
   373‚Üí                  <span className="text-primary">R$ {getTotal().toFixed(2)}</span>
   374‚Üí                </div>
   375‚Üí              </div>
   376‚Üí            </CardContent>
   377‚Üí          </Card>
   378‚Üí
   379‚Üí          {/* Checkout Form */}
   380‚Üí          <Card>
   381‚Üí            <CardHeader>
   382‚Üí              <CardTitle className="flex items-center gap-2">
   383‚Üí                <Calculator className="h-5 w-5" />
   384‚Üí                Finalizar Venda
   385‚Üí              </CardTitle>
   386‚Üí            </CardHeader>
   387‚Üí            <CardContent className="space-y-4">
   388‚Üí              <div className="space-y-2">
   389‚Üí                <Label htmlFor="barber">Barbeiro *</Label>
   390‚Üí                <Select value={selectedBarber} onValueChange={setSelectedBarber}>
   391‚Üí                  <SelectTrigger>
   392‚Üí                    <SelectValue placeholder="Selecione o barbeiro" />
   393‚Üí                  </SelectTrigger>
   394‚Üí                  <SelectContent>
   395‚Üí                    {mockBarbers.map((barber) => (
   396‚Üí                      <SelectItem key={barber.id} value={barber.id.toString()}>
   397‚Üí                        {barber.name}
   398‚Üí                      </SelectItem>
   399‚Üí                    ))}
   400‚Üí                  </SelectContent>
   401‚Üí                </Select>
   402‚Üí              </div>
   403‚Üí
   404‚Üí              <div className="space-y-2">
   405‚Üí                <Label htmlFor="client">Cliente (opcional)</Label>
   406‚Üí                <Select value={selectedClient} onValueChange={setSelectedClient}>
   407‚Üí                  <SelectTrigger>
   408‚Üí                    <SelectValue placeholder="Selecione o cliente" />
   409‚Üí                  </SelectTrigger>
   410‚Üí                  <SelectContent>
   411‚Üí                    {mockClients.map((client) => (
   412‚Üí                      <SelectItem key={client.id} value={client.id.toString()}>
   413‚Üí                        {client.name}
   414‚Üí                      </SelectItem>
   415‚Üí                    ))}
   416‚Üí                  </SelectContent>
   417‚Üí                </Select>
   418‚Üí              </div>
   419‚Üí
   420‚Üí              <div className="space-y-2">
   421‚Üí                <Label htmlFor="payment">Forma de Pagamento *</Label>
   422‚Üí                <Select value={selectedPayment} onValueChange={setSelectedPayment}>
   423‚Üí                  <SelectTrigger>
   424‚Üí                    <SelectValue placeholder="Selecione a forma" />
   425‚Üí                  </SelectTrigger>
   426‚Üí                  <SelectContent>
   427‚Üí                    <SelectItem value="dinheiro">
   428‚Üí                      <span className="flex items-center gap-2">
   429‚Üí                        <Banknote className="h-4 w-4" />
   430‚Üí                        Dinheiro
   431‚Üí                      </span>
   432‚Üí                    </SelectItem>
   433‚Üí                    <SelectItem value="cartao">
   434‚Üí                      <span className="flex items-center gap-2">
   435‚Üí                        <CreditCard className="h-4 w-4" />
   436‚Üí                        Cart√£o
   437‚Üí                      </span>
   438‚Üí                    </SelectItem>
   439‚Üí                    <SelectItem value="pix">PIX</SelectItem>
   440‚Üí                  </SelectContent>
   441‚Üí                </Select>
   442‚Üí              </div>
   443‚Üí
   444‚Üí              <Button 
   445‚Üí                onClick={finalizeSale} 
   446‚Üí                className="w-full" 
   447‚Üí                size="lg"
   448‚Üí                disabled={cart.length === 0}
