  const services = servicesResponse?.data || [];

  const updateAppointmentMutation = useMutation({
    mutationFn: async (data: UpdateAppointmentData) => {
      if (!appointment) throw new Error('Agendamento n√£o encontrado');
      return await apiService.appointments.update(appointment.id, data);
    },
    onSuccess: () => {
      toast({
        title: "Agendamento atualizado com sucesso!",
        description: "As altera√ß√µes foram salvas no sistema.",
      });
      queryClient.invalidateQueries({ queryKey: ['appointments'] });
      onOpenChange(false);

