// Interface definitions
interface Product {
  id: string;
  name: string;
  price: number;
  stock_quantity?: number;
  is_active: boolean;
}

interface Service {
  id: string;
  name: string;
  price: number;
  duration: number;
  is_active: boolean;
}

interface Barber {
  id: string;
