}

interface Client {
  id: string;
  name: string;
  email?: string;
  phone?: string;
}

interface CartItem {
  id: string;
  name: string;
  price: number;
  category: 'service' | 'product';
  quantity: number;
  duration?: number;
  stock_quantity?: number;
}

interface Sale {
  id?: string;
  barbershop_id: string;
  barber_id: string;
  client_id?: string;
  client_name?: string;
  total_amount: number;
  payment_method: 'cash' | 'card' | 'pix';
  notes?: string;
  items: {
    type: 'service' | 'product';
    service_id?: string;
    product_id?: string;
    quantity: number;
    unit_price: number;
  }[];
}

const PDV = () => {
  const { currentBarbershop } = useBarbershop();
  const queryClient = useQueryClient();

  // State management
  const [cart, setCart] = useState<CartItem[]>([]);
  const [selectedBarber, setSelectedBarber] = useState('');
  const [selectedClient, setSelectedClient] = useState('');
