import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Plus, Search, Receipt, Calendar, User, DollarSign, Eye, Trash2, Loader2, AlertTriangle, CreditCard, Banknote, Smartphone } from 'lucide-react';
import { useBarbershop } from '@/contexts/BarbershopContext';
import { apiService } from '@/lib/api';
import CreateSaleModal from '@/components/modals/CreateSaleModal';
import ViewSaleModal from '@/components/modals/ViewSaleModal';

const Sales = () => {
  const { currentBarbershop } = useBarbershop();
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isViewModalOpen, setIsViewModalOpen] = useState(false);
  const [selectedSale, setSelectedSale] = useState<any>(null);

  // Fetch sales from API
  const { data: salesData, isLoading, error } = useQuery({
    queryKey: ['sales', currentBarbershop?.id, selectedDate],
    queryFn: () => {
      if (!currentBarbershop) return Promise.reject('No barbershop selected');
      const params = selectedDate ? { date: selectedDate } : {};
      return apiService.sales.getAll(currentBarbershop.id, params);
    },
    enabled: !!currentBarbershop?.id,
  });

  // Get sales from API or fallback to empty array
  const sales = salesData?.data?.map(sale => ({
    id: sale.id,
    clientName: sale.client_name,
    barber: sale.barber_name || 'N√£o informado',
    date: sale.date || sale.created_at,
    time: sale.date ? new Date(sale.date).toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' }) : 'N/A',
    total: sale.total_amount,
    paymentMethod: sale.payment_method,
    status: sale.status || 'completed',
    items: sale.items || []

