    },
    getById: (id: string) => apiClient.get<ApiResponse<Product>>(`/api/products/${id}`),
    create: (data: CreateProductData) => apiClient.post<ApiResponse<Product>>('/api/products', data),
    update: (id: string, data: UpdateProductData) => apiClient.put<ApiResponse<Product>>(`/api/products/${id}`, data),
    updateStock: (id: string, data: StockMovementData) =>
      apiClient.post<ApiResponse<StockUpdate>>(`/api/products/${id}/stock`, data),
  },

  // Sales
  sales: {
    getAll: (barbershopId: string, params?: SalesParams) => {
      const query = new URLSearchParams({ barbershop_id: barbershopId, ...params });
      return apiClient.get<ApiResponse<Sale[]>>(`/api/sales?${query}`);
