const API_BASE = import.meta.env.VITE_API_BASE || '';

class ApiClient {
  private baseUrl: string;

  constructor(baseUrl: string = API_BASE) {
    this.baseUrl = baseUrl;
  }

  async get<T>(endpoint: string): Promise<T> {
    try {
      const response = await fetch(`${this.baseUrl}${endpoint}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      return await response.json();
    } catch (error) {
      console.error(`API Error [GET ${endpoint}]:`, error);
      throw error;
    }
  }

  async post<T>(endpoint: string, data: any): Promise<T> {
    try {
      const response = await fetch(`${this.baseUrl}${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      return await response.json();
    } catch (error) {
      console.error(`API Error [POST ${endpoint}]:`, error);
      throw error;
    }
  }

  async put<T>(endpoint: string, data: any): Promise<T> {
    try {
      const response = await fetch(`${this.baseUrl}${endpoint}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      return await response.json();
    } catch (error) {
      console.error(`API Error [PUT ${endpoint}]:`, error);
      throw error;
    }
  }

  async delete<T>(endpoint: string, data?: any): Promise<T> {
    try {
      const response = await fetch(`${this.baseUrl}${endpoint}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        body: data ? JSON.stringify(data) : undefined,
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      return await response.json();
    } catch (error) {
      console.error(`API Error [DELETE ${endpoint}]:`, error);
      throw error;
    }
  }
}

// Inst√¢ncia singleton
export const apiClient = new ApiClient();

// API Service Functions
export const apiService = {
  // Barbershops
  barbershops: {
    getAll: () => apiClient.get<ApiResponse<Barbershop[]>>('/api/barbershops'),
    getById: (id: string) => apiClient.get<ApiResponse<Barbershop>>(`/api/barbershops/${id}`),
    create: (data: CreateBarbershopData) => apiClient.post<ApiResponse<Barbershop>>('/api/barbershops', data),
    update: (id: string, data: UpdateBarbershopData) => apiClient.put<ApiResponse<Barbershop>>(`/api/barbershops/${id}`, data),
    getDashboard: (id: string) => apiClient.get<ApiResponse<BarbershopDashboard>>(`/api/barbershops/${id}/dashboard`),
  },

  // Clients
  clients: {
    getAll: (barbershopId: string, params?: ClientsParams) => {
      const query = new URLSearchParams({ barbershop_id: barbershopId, ...params });
      return apiClient.get<ApiResponse<Client[]>>(`/api/clients?${query}`);
    },
    getById: (id: string) => apiClient.get<ApiResponse<Client>>(`/api/clients/${id}`),
    create: (data: CreateClientData) => apiClient.post<ApiResponse<Client>>('/api/clients', data),
    update: (id: string, data: UpdateClientData) => apiClient.put<ApiResponse<Client>>(`/api/clients/${id}`, data),
    delete: (id: string) => apiClient.delete<ApiResponse<any>>(`/api/clients/${id}`),
  },

  // Barbers
  barbers: {
    getAll: (barbershopId: string) => {
      const query = new URLSearchParams({ barbershop_id: barbershopId });
      return apiClient.get<ApiResponse<Barber[]>>(`/api/barbers?${query}`);
    },
    getById: (id: string) => apiClient.get<ApiResponse<Barber>>(`/api/barbers/${id}`),
    create: (data: CreateBarberData) => apiClient.post<ApiResponse<Barber>>('/api/barbers', data),
    update: (id: string, data: UpdateBarberData) => apiClient.put<ApiResponse<Barber>>(`/api/barbers/${id}`, data),
    delete: (id: string) => apiClient.delete<ApiResponse<void>>(`/api/barbers/${id}`),
    getAvailableSlots: (barberId: string, date: string) => {
      const query = new URLSearchParams({ barber_id: barberId, date });
      return apiClient.get<ApiResponse<string[]>>(`/api/barbers/available-slots?${query}`);
    },
  },

  // Services
  services: {
    getAll: (barbershopId: string) => {
      const query = new URLSearchParams({ barbershop_id: barbershopId });
      return apiClient.get<ApiResponse<Service[]>>(`/api/services?${query}`);
    },
    getById: (id: string) => apiClient.get<ApiResponse<Service>>(`/api/services/${id}`),
    create: (data: CreateServiceData) => apiClient.post<ApiResponse<Service>>('/api/services', data),
    update: (id: string, data: UpdateServiceData) => apiClient.put<ApiResponse<Service>>(`/api/services/${id}`, data),
  },

  // Appointments
  appointments: {
    getAll: (barbershopId: string, params?: AppointmentsParams) => {
      const query = new URLSearchParams({ barbershop_id: barbershopId, ...params });
      return apiClient.get<ApiResponse<Appointment[]>>(`/api/appointments?${query}`);
    },
    getById: (id: string) => apiClient.get<ApiResponse<Appointment>>(`/api/appointments/${id}`),
    create: (data: CreateAppointmentData) => apiClient.post<ApiResponse<Appointment>>('/api/appointments', data),
    update: (id: string, data: UpdateAppointmentData) => apiClient.put<ApiResponse<Appointment>>(`/api/appointments/${id}`, data),
    updateStatus: (id: string, status: string) => apiClient.put<ApiResponse<Appointment>>(`/api/appointments/${id}/status`, { status }),
    delete: (id: string, reason?: string) => apiClient.delete<ApiResponse<any>>(`/api/appointments/${id}`, reason ? { reason } : undefined),
    getCalendar: (barbershopId: string, params?: CalendarParams) => {
      const query = new URLSearchParams({ barbershop_id: barbershopId, ...params });
      return apiClient.get<ApiResponse<CalendarData>>(`/api/appointments/calendar?${query}`);
    },
  },

  // Products
  products: {
    getAll: (barbershopId: string, params?: ProductsParams) => {
      const query = new URLSearchParams({ barbershop_id: barbershopId, ...params });
      return apiClient.get<ApiResponse<Product[]>>(`/api/products?${query}`);
    },
    getById: (id: string) => apiClient.get<ApiResponse<Product>>(`/api/products/${id}`),
    create: (data: CreateProductData) => apiClient.post<ApiResponse<Product>>('/api/products', data),
    update: (id: string, data: UpdateProductData) => apiClient.put<ApiResponse<Product>>(`/api/products/${id}`, data),
    delete: (id: string) => apiClient.delete<ApiResponse<void>>(`/api/products/${id}`),
    updateStock: (id: string, data: StockMovementData) =>
      apiClient.post<ApiResponse<StockUpdate>>(`/api/products/${id}/stock`, data),
  },

  // Sales
  sales: {
    getAll: (barbershopId: string, params?: SalesParams) => {
      const query = new URLSearchParams({ barbershop_id: barbershopId, ...params });
      return apiClient.get<ApiResponse<Sale[]>>(`/api/sales?${query}`);
    },
    getById: (id: string) => apiClient.get<ApiResponse<SaleWithItems>>(`/api/sales/${id}`),
    create: (data: CreateSaleData) => apiClient.post<ApiResponse<Sale>>('/api/sales', data),
    getDaily: (barbershopId: string, date?: string) => {
      const query = new URLSearchParams({ barbershop_id: barbershopId, ...(date && { date }) });
      return apiClient.get<ApiResponse<DailySales>>(`/api/sales/daily?${query}`);
    },
    getReports: (barbershopId: string, period?: string) => {
      const query = new URLSearchParams({ barbershop_id: barbershopId, ...(period && { period }) });
      return apiClient.get<ApiResponse<SalesReports>>(`/api/sales/reports?${query}`);
    },
  },

  // Legacy endpoint for dashboard
  data: () => apiClient.get<ApiDataResponse>('/api/data'),
};

