import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Plus, Search, Package, AlertTriangle, DollarSign, MoreHorizontal, Edit, Trash2, Eye } from 'lucide-react';
import { useBarbershop } from '@/contexts/BarbershopContext';
import CreateProductModal from '@/components/modals/CreateProductModal';
import EditProductModal from '@/components/modals/EditProductModal';
import ViewProductModal from '@/components/modals/ViewProductModal';

const Products = () => {
  const { currentBarbershop } = useBarbershop();
  const [searchTerm, setSearchTerm] = useState('');
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [isViewModalOpen, setIsViewModalOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Mock data - TODO: Replace with real data from Supabase
  const products = [
    {
      id: '1',
      name: 'Pomada Modeladora',
      description: 'Pomada premium para cabelo',
      brand: 'BarberLine',
      category: 'Cabelo',
      price: 45,
      cost: 25,
      stock: 15,
      minStock: 5,
      isActive: true,
      soldThisMonth: 8
    },
    {
      id: '2',
      name: 'Ã“leo para Barba',
      description: 'Ã“leo hidratante para barba',
      brand: 'BeardCare',
      category: 'Barba',
      price: 35,
      cost: 18,
      stock: 3,
      minStock: 5,
      isActive: true,
      soldThisMonth: 12
    },
    {
      id: '3',
      name: 'Shampoo Anticaspa',
      description: 'Shampoo medicinal anticaspa',
      brand: 'CleanHair',
      category: 'Cabelo',
      price: 28,
      cost: 15,
      stock: 22,
      minStock: 8,
      isActive: true,
      soldThisMonth: 5
    },
    {
      id: '4',
      name: 'Gel Fixador Forte',
      description: 'Gel com fixaÃ§Ã£o extra forte',
      brand: 'StyleMax',
      category: 'Cabelo',
      price: 32,
      cost: 20,
      stock: 0,
      minStock: 10,
      isActive: true,
      soldThisMonth: 0
    },
    {
      id: '5',
      name: 'Cera Modeladora',
      description: 'Cera para modelagem e brilho',
      brand: 'BarberLine',
      category: 'Cabelo',
      price: 38,
      cost: 22,
      stock: 18,
      minStock: 6,
      isActive: false,
      soldThisMonth: 2
    }
  ];

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    product.brand.toLowerCase().includes(searchTerm.toLowerCase()) ||
    product.category.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const stats = {
    totalProducts: products.length,
    activeProducts: products.filter(p => p.isActive).length,
    lowStockProducts: products.filter(p => p.stock <= p.minStock).length,
    totalValue: products.reduce((acc, p) => acc + (p.price * p.stock), 0),
    monthlyRevenue: products.reduce((acc, p) => acc + (p.price * p.soldThisMonth), 0)
  };

  const getStockStatus = (product: typeof products[0]) => {
    if (product.stock === 0) {
      return <Badge variant="destructive">Sem Estoque</Badge>;
    }
    if (product.stock <= product.minStock) {
      return <Badge className="bg-orange-100 text-orange-800">Estoque Baixo</Badge>;
    }
    return <Badge className="bg-green-100 text-green-800">Em Estoque</Badge>;
  };

  const handleViewProduct = (product: any) => {
    setSelectedProduct(product);
    setIsViewModalOpen(true);
  };

  const handleEditProduct = (product: any) => {
    setSelectedProduct(product);
    setIsEditModalOpen(true);
  };

  const handleEditFromView = () => {
    setIsViewModalOpen(false);
    setIsEditModalOpen(true);
  };

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Produtos</h1>
          <p className="text-muted-foreground">
            Gerencie o estoque de produtos da sua barbearia
          </p>
        </div>
        <Button className="btn-hero" onClick={() => setIsCreateModalOpen(true)}>
          <Plus className="h-4 w-4 mr-2" />
          Novo Produto
        </Button>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-6">
        <Card className="card-elegant">
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground">Total de Produtos</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-primary">{stats.totalProducts}</div>
          </CardContent>
        </Card>

        <Card className="card-elegant">
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground">Produtos Ativos</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-primary">{stats.activeProducts}</div>
          </CardContent>
        </Card>

        <Card className="card-elegant">
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground">Estoque Baixo</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-orange-600">{stats.lowStockProducts}</div>
          </CardContent>
        </Card>

        <Card className="card-elegant">
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground">Valor do Estoque</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-primary">R$ {stats.totalValue.toLocaleString()}</div>
          </CardContent>
        </Card>

        <Card className="card-elegant">
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground">Vendas do MÃªs</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-primary">R$ {stats.monthlyRevenue}</div>
          </CardContent>
        </Card>
      </div>

      {/* Search */}
      <Card className="card-elegant">
        <CardHeader>
          <CardTitle>Buscar Produtos</CardTitle>
          <CardDescription>
            Encontre produtos por nome, marca ou categoria
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="flex items-center space-x-2">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
              <Input
                placeholder="Pesquisar produtos..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
              />
            </div>
            <Button variant="outline">Filtros</Button>
          </div>
        </CardContent>
      </Card>

