  },

  // Sales
  sales: {
    getAll: (barbershopId: string, params?: SalesParams) => {
      const query = new URLSearchParams({ barbershop_id: barbershopId, ...params });
      return apiClient.get<ApiResponse<Sale[]>>(`/api/sales?${query}`);
    },
    getById: (id: string) => apiClient.get<ApiResponse<SaleWithItems>>(`/api/sales/${id}`),
    create: (data: CreateSaleData) => apiClient.post<ApiResponse<Sale>>('/api/sales', data),
    getDaily: (barbershopId: string, date?: string) => {
      const query = new URLSearchParams({ barbershop_id: barbershopId, ...(date && { date }) });
      return apiClient.get<ApiResponse<DailySales>>(`/api/sales/daily?${query}`);
    },
    getReports: (barbershopId: string, period?: string) => {
      const query = new URLSearchParams({ barbershop_id: barbershopId, ...(period && { period }) });
      return apiClient.get<ApiResponse<SalesReports>>(`/api/sales/reports?${query}`);
    },
  },

  // Legacy endpoint for dashboard
  data: () => apiClient.get<ApiDataResponse>('/api/data'),
};

