      isActive: true,
      soldThisMonth: 8
    },
    {
      id: '2',
      name: 'Ã“leo para Barba',
      description: 'Ã“leo hidratante para barba',
      brand: 'BeardCare',
      category: 'Barba',
      price: 35,
      cost: 18,
      stock: 3,
      minStock: 5,
      isActive: true,
      soldThisMonth: 12
    },
