      id: '4',
      name: 'Gel Fixador Forte',
      description: 'Gel com fixaÃ§Ã£o extra forte',
      brand: 'StyleMax',
      category: 'Cabelo',
      price: 32,
      cost: 20,
      stock: 0,
      minStock: 10,
      isActive: true,
      soldThisMonth: 0
