    setFormData({
      name: '',
      email: '',
      phone: '',
      specialties: [],
      status: 'ativo',
      workSchedule: '',
      address: '',
    });
    toast.success('Barbeiro adicionado com sucesso!');
  };

  const handleEdit = () => {
    if (!selectedBarber || !formData.name || !formData.email || !formData.phone) {
      toast.error('Preencha todos os campos obrigatÃ³rios');
      return;
    }

    setBarbers(barbers.map(barber =>
      barber.id === selectedBarber.id
        ? {
            ...barber,
            name: formData.name!,
            email: formData.email!,
            phone: formData.phone!,
            specialties: formData.specialties || [],
            status: (formData.status as 'ativo' | 'inativo' | 'ferias') || barber.status,
            workSchedule: formData.workSchedule || barber.workSchedule,
            address: formData.address || barber.address,
          }
        : barber
    ));

    setIsEditDialogOpen(false);
    setSelectedBarber(null);
    setFormData({
      name: '',
      email: '',
      phone: '',
      specialties: [],
      status: 'ativo',
      workSchedule: '',
      address: '',
    });
    toast.success('Barbeiro atualizado com sucesso!');
