  const handleDelete = (id: number) => {
    setBarbers(barbers.filter(barber => barber.id !== id));
    toast.success('Barbeiro removido com sucesso!');
  };

  const openEditDialog = (barber: Barber) => {
    setSelectedBarber(barber);
    setFormData({
      name: barber.name,
      email: barber.email,
      phone: barber.phone,
      specialties: barber.specialties,
      status: barber.status,
      workSchedule: barber.workSchedule,
      address: barber.address,
    });
    setIsEditDialogOpen(true);
  };

  const getStatusBadge = (status: string) => {
    const variants = {
      ativo: 'default',
      inativo: 'destructive',
      ferias: 'secondary',
    };
    return variants[status as keyof typeof variants] || 'default';
  };

  const handleSpecialtiesChange = (value: string) => {
    const specialtiesArray = value.split(',').map(s => s.trim()).filter(s => s);
    setFormData({ ...formData, specialties: specialtiesArray });
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
