      case 'dinheiro':
        return <Banknote className="h-4 w-4 text-green-600" />;
      case 'card':
      case 'cartÃ£o':
        return <CreditCard className="h-4 w-4 text-blue-600" />;
      case 'pix':
        return <Smartphone className="h-4 w-4 text-purple-600" />;
      default:
        return <Calculator className="h-4 w-4 text-gray-600" />;
    }
  };

