    switch (method.toLowerCase()) {
      case 'cash': return 'Dinheiro';
      case 'card': return 'CartÃ£o';
      case 'pix': return 'PIX';
      default: return method;
    }
  };

  const servicesTotal = sale.items?.filter(item => item.type === 'service')
    .reduce((sum, item) => sum + item.total_price, 0) || 0;

